#!/usr/bin/env python3

import requests
import time
import json
import sys

class InteractiveTelegramBot:
    def __init__(self, token, chat_id):
        self.token = token
        self.chat_id = chat_id
        self.base_url = f"https://api.telegram.org/bot{token}"

    def send_permission_request(self, agent_name, action_description):
        keyboard = {
            "inline_keyboard": [[
                {"text": "‚úÖ Yes", "callback_data": "yes"},
                {"text": "‚ùå No", "callback_data": "no"},
                {"text": "‚è≠Ô∏è Skip", "callback_data": "skip"}
            ]]
        }

        message = f"ü§ñ *Agent:* {agent_name}\n\nüìã *Requesting permission:*\n{action_description}\n\nApprove this action?"

        response = requests.post(
            f"{self.base_url}/sendMessage",
            json={
                "chat_id": self.chat_id,
                "text": message,
                "parse_mode": "Markdown",
                "reply_markup": keyboard
            }
        )

        return response.json()["result"]["message_id"]

    def wait_for_response(self, timeout=300):
        """Wait up to timeout seconds for user response"""
        start_time = time.time()
        last_update_id = None

        while time.time() - start_time < timeout:
            updates = requests.get(f"{self.base_url}/getUpdates", 
                                  params={"offset": last_update_id, "timeout": 10})

            for update in updates.json().get("result", []):
                last_update_id = update["update_id"] + 1

                if "callback_query" in update:
                    callback = update["callback_query"]
                    response = callback["data"]

                    # Acknowledge the button press
                    requests.post(f"{self.base_url}/answerCallbackQuery",
                                json={"callback_query_id": callback["id"]})

                    # Update message to show decision
                    requests.post(f"{self.base_url}/editMessageText",
                                json={
                                    "chat_id": self.chat_id,
                                    "message_id": callback["message"]["message_id"],
                                    "text": f"{callback['message']['text']}\n\n‚úîÔ∏è *Decision:* {response.upper()}",
                                    "parse_mode": "Markdown"
                                })

                    return response

            time.sleep(1)

        return "timeout"

if __name__ == "__main__":
    # Example usage
    bot = InteractiveTelegramBot(
        token="YOUR_BOT_TOKEN",
        chat_id="YOUR_CHAT_ID"
    )

    # Agent requests permission
    bot.send_permission_request(
        agent_name="god",
        action_description="Delete 5 test files in /tmp/old_tests/"
    )

    # Wait for user response
    response = bot.wait_for_response()

    if response == "yes":
        print("Permission granted - proceeding with action")
        sys.exit(0)
    elif response == "no":
        print("Permission denied - aborting action")
        sys.exit(1)
    elif response == "skip":
        print("Skipping this action")
        sys.exit(2)
    else:
        print("No response received - timeout")
        sys.exit(3)
