package trading

import (
	"fmt"
	"os"

	"bigmeanie/internal/types"
	"github.com/shopspring/decimal"
	"gopkg.in/yaml.v3"
)

// Config implements trading-related configuration
type Config struct {
	RunMode                  string
	TradingPairs             []string
	InitialCapital           decimal.Decimal
	TradingFees              types.TradingFees
	OrderTimeoutSeconds      int
	MaxRetries               int
	ClosePositionsOnShutdown bool
	RequireOrderValidation   bool
}

// yamlConfig is the structure for parsing YAML
type yamlConfig struct {
	Trading struct {
		InitialCapital           float64 `yaml:"initial_capital"`
		ClosePositionsOnShutdown bool    `yaml:"close_positions_on_shutdown"`
		OrderTimeoutSeconds      int     `yaml:"order_timeout_seconds"`
		MaxRetries               int     `yaml:"max_retries"`
		RequireOrderValidation   bool    `yaml:"require_order_validation"`
		Fees                     struct {
			SpotMakerFee   float64 `yaml:"spot_maker_fee"`
			SpotTakerFee   float64 `yaml:"spot_taker_fee"`
			PerpMakerFee   float64 `yaml:"perp_maker_fee"`
			PerpTakerFee   float64 `yaml:"perp_taker_fee"`
			WithdrawalFee  float64 `yaml:"withdrawal_fee"`
			SlippageFactor float64 `yaml:"slippage_factor"`
		} `yaml:"fees"`
	} `yaml:"trading"`
}

// LoadConfig loads trading configuration from YAML and environment
func LoadConfig() (*Config, error) {
	// Load YAML config
	configFile := "config/trading.yaml"
	if envFile := os.Getenv("TRADING_CONFIG_FILE"); envFile != "" {
		configFile = envFile
	}

	data, err := os.ReadFile(configFile)
	if err != nil {
		return nil, fmt.Errorf("failed to read trading config: %w", err)
	}

	var yamlCfg yamlConfig
	if err := yaml.Unmarshal(data, &yamlCfg); err != nil {
		return nil, fmt.Errorf("failed to parse trading config: %w", err)
	}

	// Build config
	cfg := &Config{
		RunMode:                  os.Getenv("RUN_MODE"), // backtest, paper, or live
		InitialCapital:           decimal.NewFromFloat(yamlCfg.Trading.InitialCapital),
		ClosePositionsOnShutdown: yamlCfg.Trading.ClosePositionsOnShutdown,
		OrderTimeoutSeconds:      yamlCfg.Trading.OrderTimeoutSeconds,
		MaxRetries:               yamlCfg.Trading.MaxRetries,
		RequireOrderValidation:   yamlCfg.Trading.RequireOrderValidation,
		TradingFees: types.TradingFees{
			SpotMakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.SpotMakerFee),
			SpotTakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.SpotTakerFee),
			PerpMakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.PerpMakerFee),
			PerpTakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.PerpTakerFee),
			WithdrawalFee:  decimal.NewFromFloat(yamlCfg.Trading.Fees.WithdrawalFee),
			SlippageFactor: decimal.NewFromFloat(yamlCfg.Trading.Fees.SlippageFactor),
		},
	}

	// Default run mode to paper if not set
	if cfg.RunMode == "" {
		cfg.RunMode = "paper"
	}

	// Validate
	validModes := []string{"backtest", "paper", "live"}
	valid := false
	for _, mode := range validModes {
		if cfg.RunMode == mode {
			valid = true
			break
		}
	}
	if !valid {
		return nil, fmt.Errorf("invalid RUN_MODE: %s (must be one of: backtest, paper, live)", cfg.RunMode)
	}

	return cfg, nil
}

// Getter methods
func (c *Config) GetRunMode() string {
	return c.RunMode
}

func (c *Config) GetTradingPairs() []string {
	return c.TradingPairs
}

func (c *Config) GetInitialCapital() decimal.Decimal {
	if c.InitialCapital.IsZero() {
		return decimal.NewFromFloat(10000) // default $10,000
	}
	return c.InitialCapital
}

func (c *Config) IsPaperMode() bool {
	return c.RunMode == "paper"
}

func (c *Config) IsRealtimeMode() bool {
	return c.RunMode == "paper" || c.RunMode == "live"
}

func (c *Config) GetRequireOrderValidation() bool {
	return c.RequireOrderValidation
}

func (c *Config) GetClosePositionsOnShutdown() bool {
	return c.ClosePositionsOnShutdown
}

func (c *Config) GetTradingFees() types.TradingFees {
	return c.TradingFees
}

package trading

import (
	"time"
	"bigmeanie/internal/types"
	"github.com/shopspring/decimal"
)

// TradingConfig defines the configuration needed for trading operations
type TradingConfig interface {
	// Core trading configuration
	GetRunMode() string
	GetTradingPairs() []string
	GetInitialCapital() decimal.Decimal
	IsPaperMode() bool
	IsRealtimeMode() bool
	GetRequireOrderValidation() bool
	GetClosePositionsOnShutdown() bool

	// Trading fee configuration
	GetTradingFees() types.TradingFees
}

// RiskConfig defines the configuration needed for risk management
type RiskConfig interface {
	// Position sizing
	GetMaxPositionSize() decimal.Decimal
	GetMaxOpenPositions() int
	GetRiskPerTrade() decimal.Decimal

	// Loss limits
	GetMaxDailyLoss() decimal.Decimal
	GetDailyLossLimit() decimal.Decimal
	GetMaxDrawdown() decimal.Decimal
	GetEmergencyDrawdownLimit() decimal.Decimal

	// Margin configuration
	GetMarginRequirement() decimal.Decimal
	GetMaxMarginRatio() decimal.Decimal

	// Stop loss configuration
	GetStopLossVolatilityMultiplier() decimal.Decimal
}

// CredentialsConfig defines the configuration for API credentials
type CredentialsConfig interface {
	GetTradingCredentials() (keyID, secret string)
}

// TimingConfig defines timing-related configuration
type TimingConfig interface {
	GetMinTimeBetweenTrades() time.Duration
}

package trading

import (
	"fmt"
	"os"

	"github.com/shopspring/decimal"
	"gopkg.in/yaml.v3"
)

// RiskSettings holds risk management configuration
type RiskSettings struct {
	MaxPositionSize              decimal.Decimal
	MaxOpenPositions             int
	RiskPerTrade                 decimal.Decimal
	MaxDailyLoss                 decimal.Decimal
	DailyLossLimit               decimal.Decimal
	MaxDrawdown                  decimal.Decimal
	EmergencyDrawdownLimit       decimal.Decimal
	MarginRequirement            decimal.Decimal
	MaxMarginRatio               decimal.Decimal
	StopLossVolatilityMultiplier decimal.Decimal
	TakeProfitLevel1Pct          decimal.Decimal
	TakeProfitLevel2Pct          decimal.Decimal
	TakeProfitLevel3Pct          decimal.Decimal
	TakeProfitRatio1             decimal.Decimal
	TakeProfitRatio2             decimal.Decimal
}

// yamlConfig is the structure for parsing YAML
type riskYamlConfig struct {
	Risk struct {
		MaxPositionSize              float64 `yaml:"max_position_size"`
		StopLossATRMultiple          float64 `yaml:"stop_loss_atr_multiple"`
		MaxDrawdown                  float64 `yaml:"max_drawdown"`
		DailyLossLimit               float64 `yaml:"daily_loss_limit"`
		MaxOpenPositions             int     `yaml:"max_open_positions"`
		MinAccountBalance            float64 `yaml:"min_account_balance"`
		MarginRequirement            float64 `yaml:"margin_requirement"`
		MaxMarginRatio               float64 `yaml:"max_margin_ratio"`
		EmergencyDrawdownLimit       float64 `yaml:"emergency_drawdown_limit"`
		RiskPerTrade                 float64 `yaml:"risk_per_trade"`
		StopLossVolatilityMultiplier float64 `yaml:"stop_loss_volatility_multiplier"`
		TakeProfitLevel1Pct          float64 `yaml:"take_profit_level1_pct"`
		TakeProfitLevel2Pct          float64 `yaml:"take_profit_level2_pct"`
		TakeProfitLevel3Pct          float64 `yaml:"take_profit_level3_pct"`
		TakeProfitRatio1             float64 `yaml:"take_profit_ratio1"`
		TakeProfitRatio2             float64 `yaml:"take_profit_ratio2"`
	} `yaml:"risk"`
}

// LoadRiskSettings loads risk configuration from YAML
func LoadRiskSettings() (*RiskSettings, error) {
	// Load YAML config
	configFile := "config/risk.yaml"
	if envFile := os.Getenv("RISK_CONFIG_FILE"); envFile != "" {
		configFile = envFile
	}

	data, err := os.ReadFile(configFile)
	if err != nil {
		return nil, fmt.Errorf("failed to read risk config: %w", err)
	}

	var yamlCfg riskYamlConfig
	if err := yaml.Unmarshal(data, &yamlCfg); err != nil {
		return nil, fmt.Errorf("failed to parse risk config: %w", err)
	}

	// Build config
	cfg := &RiskSettings{
		MaxPositionSize:              decimal.NewFromFloat(yamlCfg.Risk.MaxPositionSize),
		MaxOpenPositions:             yamlCfg.Risk.MaxOpenPositions,
		RiskPerTrade:                 decimal.NewFromFloat(yamlCfg.Risk.RiskPerTrade),
		MaxDailyLoss:                 decimal.NewFromFloat(yamlCfg.Risk.DailyLossLimit),
		DailyLossLimit:               decimal.NewFromFloat(yamlCfg.Risk.DailyLossLimit),
		MaxDrawdown:                  decimal.NewFromFloat(yamlCfg.Risk.MaxDrawdown),
		EmergencyDrawdownLimit:       decimal.NewFromFloat(yamlCfg.Risk.EmergencyDrawdownLimit),
		MarginRequirement:            decimal.NewFromFloat(yamlCfg.Risk.MarginRequirement),
		MaxMarginRatio:               decimal.NewFromFloat(yamlCfg.Risk.MaxMarginRatio),
		StopLossVolatilityMultiplier: decimal.NewFromFloat(yamlCfg.Risk.StopLossVolatilityMultiplier),
		TakeProfitLevel1Pct:          decimal.NewFromFloat(yamlCfg.Risk.TakeProfitLevel1Pct),
		TakeProfitLevel2Pct:          decimal.NewFromFloat(yamlCfg.Risk.TakeProfitLevel2Pct),
		TakeProfitLevel3Pct:          decimal.NewFromFloat(yamlCfg.Risk.TakeProfitLevel3Pct),
		TakeProfitRatio1:             decimal.NewFromFloat(yamlCfg.Risk.TakeProfitRatio1),
		TakeProfitRatio2:             decimal.NewFromFloat(yamlCfg.Risk.TakeProfitRatio2),
	}

	return cfg, nil
}

// Getter methods
func (s *RiskSettings) GetMaxPositionSize() decimal.Decimal {
	return s.MaxPositionSize
}

func (s *RiskSettings) GetMaxOpenPositions() int {
	return s.MaxOpenPositions
}

func (s *RiskSettings) GetRiskPerTrade() decimal.Decimal {
	return s.RiskPerTrade
}

func (s *RiskSettings) GetMaxDailyLoss() decimal.Decimal {
	return s.MaxDailyLoss
}

func (s *RiskSettings) GetDailyLossLimit() decimal.Decimal {
	return s.DailyLossLimit
}

func (s *RiskSettings) GetMaxDrawdown() decimal.Decimal {
	return s.MaxDrawdown
}

func (s *RiskSettings) GetEmergencyDrawdownLimit() decimal.Decimal {
	return s.EmergencyDrawdownLimit
}

func (s *RiskSettings) GetMarginRequirement() decimal.Decimal {
	return s.MarginRequirement
}

func (s *RiskSettings) GetMaxMarginRatio() decimal.Decimal {
	return s.MaxMarginRatio
}

func (s *RiskSettings) GetStopLossVolatilityMultiplier() decimal.Decimal {
	return s.StopLossVolatilityMultiplier
}

func (s *RiskSettings) GetTakeProfitLevel1Pct() decimal.Decimal {
	return s.TakeProfitLevel1Pct
}

func (s *RiskSettings) GetTakeProfitLevel2Pct() decimal.Decimal {
	return s.TakeProfitLevel2Pct
}

func (s *RiskSettings) GetTakeProfitLevel3Pct() decimal.Decimal {
	return s.TakeProfitLevel3Pct
}

func (s *RiskSettings) GetTakeProfitRatio1() decimal.Decimal {
	return s.TakeProfitRatio1
}

func (s *RiskSettings) GetTakeProfitRatio2() decimal.Decimal {
	return s.TakeProfitRatio2
}

package config

import (
	"fmt"
	"os"

	"bigmeanie/internal/api"
	"bigmeanie/internal/data"
	"bigmeanie/internal/database"
	"bigmeanie/internal/strategy"
	"bigmeanie/internal/trading"
	"bigmeanie/pkg/logger"

	"github.com/joho/godotenv"
)

// GlobalConfig holds all domain-specific configurations
type GlobalConfig struct {
	Database *database.Config
	API      *api.Config
	Trading  *trading.Config
	Risk     *trading.RiskSettings
	Strategy *strategy.Config
	Data     *data.Config
}

// LoadGlobalConfig loads all domain-specific configurations
func LoadGlobalConfig() (*GlobalConfig, error) {
	// Load .env file for credentials if it exists
	if err := godotenv.Load(); err != nil {
		logger.Component("config").Infow("No .env file found or error loading it", "error", err)
	}

	// Set environment from env var
	environment := os.Getenv("ENVIRONMENT")
	if environment == "" {
		environment = "development"
	}
	logger.Component("config").Infow("Loading configuration", "environment", environment)

	// Load each domain's configuration
	dbConfig, err := database.LoadConfig()
	if err != nil {
		return nil, fmt.Errorf("failed to load database config: %w", err)
	}

	apiConfig, err := api.LoadConfig()
	if err != nil {
		return nil, fmt.Errorf("failed to load API config: %w", err)
	}

	// Load strategy config first since trading config needs trading pairs from it
	strategyConfig, err := strategy.LoadConfig()
	if err != nil {
		return nil, fmt.Errorf("failed to load strategy config: %w", err)
	}

	tradingConfig, err := trading.LoadConfig()
	if err != nil {
		return nil, fmt.Errorf("failed to load trading config: %w", err)
	}
	// Set trading pairs from strategy config
	tradingConfig.TradingPairs = strategyConfig.TradingPairs

	riskConfig, err := trading.LoadRiskSettings()
	if err != nil {
		return nil, fmt.Errorf("failed to load risk config: %w", err)
	}

	dataConfig, err := data.LoadConfig()
	if err != nil {
		return nil, fmt.Errorf("failed to load data config: %w", err)
	}

	// Apply environment-specific overrides
	switch environment {
	case "production":
		// Production safety measures
		tradingConfig.ClosePositionsOnShutdown = true
		dbConfig.LogLevel = "warn"
		// Larger connection pools for production
		if dbConfig.MaxIdleConns < 50 {
			dbConfig.MaxIdleConns = 50
		}
		if dbConfig.MaxOpenConns < 200 {
			dbConfig.MaxOpenConns = 200
		}
	case "development":
		// Development settings
		if dbConfig.LogLevel == "" {
			dbConfig.LogLevel = "debug"
		}
	}

	return &GlobalConfig{
		Database: dbConfig,
		API:      apiConfig,
		Trading:  tradingConfig,
		Risk:     riskConfig,
		Strategy: strategyConfig,
		Data:     dataConfig,
	}, nil
}

