package strategy

import (
	"fmt"
	"time"

	"bigmeanie/internal/common"
	"bigmeanie/internal/models"

	"gorm.io/gorm"
)

// StrategyStore implements database.Strategy interface
type StrategyStore struct {
	db *gorm.DB
}

// NewStrategyStore creates a new strategy store
func NewStrategyStore(db *gorm.DB) *StrategyStore {
	return &StrategyStore{db: db}
}

// CreateStrategyConfig creates a new strategy configuration
func (r *StrategyStore) CreateStrategyConfig(config *models.StrategyConfig) error {
	return r.db.Create(config).Error
}

// GetStrategyConfigByID retrieves a strategy configuration by ID
func (r *StrategyStore) GetStrategyConfigByID(id uint) (*models.StrategyConfig, error) {
	var config models.StrategyConfig
	err := r.db.Preload("RiskSettings").
		Preload("TechIndicators").
		Preload("EntryExit").
		Preload("Universe.Instruments").
		First(&config, id).Error
	if err != nil {
		return nil, err
	}
	return &config, nil
}

// GetStrategyConfigByName retrieves a strategy configuration by name
func (r *StrategyStore) GetStrategyConfigByName(name string) (*models.StrategyConfig, error) {
	var config models.StrategyConfig
	err := r.db.Preload("RiskSettings").
		Preload("TechIndicators").
		Preload("EntryExit").
		Preload("Universe.Instruments").
		Where("name = ?", name).First(&config).Error
	if err != nil {
		return nil, err
	}
	return &config, nil
}

// UpdateStrategyConfig updates an existing strategy configuration
func (r *StrategyStore) UpdateStrategyConfig(config *models.StrategyConfig) error {
	return r.db.Save(config).Error
}

// GetAllStrategyConfigs retrieves all strategy configurations
func (r *StrategyStore) GetAllStrategyConfigs() ([]*models.StrategyConfig, error) {
	var configs []*models.StrategyConfig
	err := r.db.Find(&configs).Error
	return configs, err
}

// GetActiveStrategyConfigs retrieves all active strategy configurations
func (r *StrategyStore) GetActiveStrategyConfigs() ([]models.StrategyConfig, error) {
	var configs []models.StrategyConfig
	err := r.db.Where("is_active = ?", true).Find(&configs).Error
	return configs, err
}

// GetStrategyConfig retrieves a strategy configuration by name and type
func (r *StrategyStore) GetStrategyConfig(name, strategyType string) (*models.StrategyConfig, error) {
	var config models.StrategyConfig
	err := r.db.Where("name = ? AND strategy_type = ?", name, strategyType).First(&config).Error
	if err != nil {
		return nil, err
	}
	return &config, nil
}

// CreateStrategyState creates a new strategy state
func (r *StrategyStore) CreateStrategyState(state *models.StrategyState) error {
	return r.db.Create(state).Error
}

// GetStrategyState retrieves the current state for a strategy
func (r *StrategyStore) GetStrategyState(symbol string) (*models.StrategyState, error) {
	var state models.StrategyState
	err := r.db.Where("symbol = ?", symbol).
		Order("created_at DESC").
		First(&state).Error
	if err != nil {
		if err == gorm.ErrRecordNotFound {
			// Return error to indicate no state exists
			return nil, common.ErrNotFound
		}
		return nil, err
	}
	return &state, nil
}

// UpdateStrategyState updates an existing strategy state
func (r *StrategyStore) UpdateStrategyState(state *models.StrategyState) error {
	return r.db.Save(state).Error
}

// SaveStrategyState saves a new strategy state snapshot
func (r *StrategyStore) SaveStrategyState(strategyConfigID uint, state interface{}) error {
	// Type assert to StrategyState
	strategyState, ok := state.(*models.StrategyState)
	if !ok {
		return fmt.Errorf("state must be *models.StrategyState")
	}

	// Update the timestamp
	strategyState.LastUpdateTime = time.Now()

	return r.CreateStrategyState(strategyState)
}

// GetStrategyStateHistory retrieves historical states for a strategy
func (r *StrategyStore) GetStrategyStateHistory(strategyConfigID uint, limit int) ([]*models.StrategyState, error) {
	var states []*models.StrategyState
	query := r.db.Where("strategy_config_id = ?", strategyConfigID).
		Order("created_at DESC")

	if limit > 0 {
		query = query.Limit(limit)
	}

	err := query.Find(&states).Error
	return states, err
}

// DeleteOldStrategyStates deletes strategy states older than the specified time
func (r *StrategyStore) DeleteOldStrategyStates(before time.Time) error {
	return r.db.Where("created_at < ?", before).Delete(&models.StrategyState{}).Error
}

// GetStrategyConfigsByType retrieves all strategy configurations of a specific type
func (r *StrategyStore) GetStrategyConfigsByType(strategyType string) ([]*models.StrategyConfig, error) {
	var configs []*models.StrategyConfig
	err := r.db.Where("strategy_type = ?", strategyType).Find(&configs).Error
	return configs, err
}

// GetStrategyConfigsBySymbol retrieves all strategy configurations for a specific symbol
func (r *StrategyStore) GetStrategyConfigsBySymbol(symbol string) ([]*models.StrategyConfig, error) {
	var configs []*models.StrategyConfig
	// This assumes parameters are stored as JSON and contain a "symbol" field
	err := r.db.Where("JSON_EXTRACT(parameters, '$.symbol') = ?", symbol).Find(&configs).Error
	return configs, err
}

