package trading

import (
	"fmt"
	"os"

	"bigmeanie/internal/types"
	"github.com/shopspring/decimal"
	"gopkg.in/yaml.v3"
)

// Config implements trading-related configuration
type Config struct {
	RunMode                  string
	TradingPairs             []string
	InitialCapital           decimal.Decimal
	TradingFees              types.TradingFees
	OrderTimeoutSeconds      int
	MaxRetries               int
	ClosePositionsOnShutdown bool
	RequireOrderValidation   bool
}

// yamlConfig is the structure for parsing YAML
type yamlConfig struct {
	Trading struct {
		InitialCapital           float64 `yaml:"initial_capital"`
		ClosePositionsOnShutdown bool    `yaml:"close_positions_on_shutdown"`
		OrderTimeoutSeconds      int     `yaml:"order_timeout_seconds"`
		MaxRetries               int     `yaml:"max_retries"`
		RequireOrderValidation   bool    `yaml:"require_order_validation"`
		Fees                     struct {
			SpotMakerFee   float64 `yaml:"spot_maker_fee"`
			SpotTakerFee   float64 `yaml:"spot_taker_fee"`
			PerpMakerFee   float64 `yaml:"perp_maker_fee"`
			PerpTakerFee   float64 `yaml:"perp_taker_fee"`
			WithdrawalFee  float64 `yaml:"withdrawal_fee"`
			SlippageFactor float64 `yaml:"slippage_factor"`
		} `yaml:"fees"`
	} `yaml:"trading"`
}

// LoadConfig loads trading configuration from YAML and environment
func LoadConfig() (*Config, error) {
	// Load YAML config
	configFile := "config/trading.yaml"
	if envFile := os.Getenv("TRADING_CONFIG_FILE"); envFile != "" {
		configFile = envFile
	}

	data, err := os.ReadFile(configFile)
	if err != nil {
		return nil, fmt.Errorf("failed to read trading config: %w", err)
	}

	var yamlCfg yamlConfig
	if err := yaml.Unmarshal(data, &yamlCfg); err != nil {
		return nil, fmt.Errorf("failed to parse trading config: %w", err)
	}

	// Build config
	cfg := &Config{
		RunMode:                  os.Getenv("RUN_MODE"), // backtest, paper, or live
		InitialCapital:           decimal.NewFromFloat(yamlCfg.Trading.InitialCapital),
		ClosePositionsOnShutdown: yamlCfg.Trading.ClosePositionsOnShutdown,
		OrderTimeoutSeconds:      yamlCfg.Trading.OrderTimeoutSeconds,
		MaxRetries:               yamlCfg.Trading.MaxRetries,
		RequireOrderValidation:   yamlCfg.Trading.RequireOrderValidation,
		TradingFees: types.TradingFees{
			SpotMakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.SpotMakerFee),
			SpotTakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.SpotTakerFee),
			PerpMakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.PerpMakerFee),
			PerpTakerFee:   decimal.NewFromFloat(yamlCfg.Trading.Fees.PerpTakerFee),
			WithdrawalFee:  decimal.NewFromFloat(yamlCfg.Trading.Fees.WithdrawalFee),
			SlippageFactor: decimal.NewFromFloat(yamlCfg.Trading.Fees.SlippageFactor),
		},
	}

	// Default run mode to paper if not set
	if cfg.RunMode == "" {
		cfg.RunMode = "paper"
	}

	// Validate
	validModes := []string{"backtest", "paper", "live"}
	valid := false
	for _, mode := range validModes {
		if cfg.RunMode == mode {
			valid = true
			break
		}
	}
	if !valid {
		return nil, fmt.Errorf("invalid RUN_MODE: %s (must be one of: backtest, paper, live)", cfg.RunMode)
	}

	return cfg, nil
}

// Getter methods
func (c *Config) GetRunMode() string {
	return c.RunMode
}

func (c *Config) GetTradingPairs() []string {
	return c.TradingPairs
}

func (c *Config) GetInitialCapital() decimal.Decimal {
	if c.InitialCapital.IsZero() {
		return decimal.NewFromFloat(10000) // default $10,000
	}
	return c.InitialCapital
}

func (c *Config) IsPaperMode() bool {
	return c.RunMode == "paper"
}

func (c *Config) IsRealtimeMode() bool {
	return c.RunMode == "paper" || c.RunMode == "live"
}

func (c *Config) GetRequireOrderValidation() bool {
	return c.RequireOrderValidation
}

func (c *Config) GetClosePositionsOnShutdown() bool {
	return c.ClosePositionsOnShutdown
}

func (c *Config) GetTradingFees() types.TradingFees {
	return c.TradingFees
}

