)

// ParameterLoader handles loading and caching of strategy parameters
type ParameterLoader struct {
	cache sync.Map // map[uint]*StrategyParameters - keyed by StrategyConfig.ID
	mu    sync.RWMutex
}

// NewParameterLoader creates a new parameter loader
func NewParameterLoader() *ParameterLoader {
	return &ParameterLoader{}
}

// LoadParameters loads parameters from a StrategyConfig, using cache if available
func (l *ParameterLoader) LoadParameters(config *models.StrategyConfig) (*StrategyParameters, error) {
	if config == nil {
		return nil, fmt.Errorf("strategy config is nil")
	}

	// Check cache first
	if cached, ok := l.cache.Load(config.ID); ok {
		return cached.(*StrategyParameters), nil
	}

	// Create parameters from normalized structure
	params := &StrategyParameters{
		Risk: RiskParameters{
			MaxPositionSize:       config.RiskSettings.MaxPositionSize,
			RiskPerTrade:          config.RiskSettings.RiskPerTrade,
			StopLossPercent:       config.RiskSettings.StopLossPercent,
			TakeProfitPercent:     config.RiskSettings.TakeProfitPercent,
			StopLossATRMultiple:   config.RiskSettings.StopLossATRMultiple,
			TakeProfitATRMultiple: config.RiskSettings.TakeProfitATRMultiple,
			MaxOpenPositions:      config.RiskSettings.MaxOpenPositions,
			MaxDrawdownLimit:      config.RiskSettings.MaxDrawdownLimit,
			DailyLossLimit:        config.RiskSettings.DailyLossLimit,
			UseTrailingStop:       config.RiskSettings.UseTrailingStop,
			TrailingStopPercent:   config.RiskSettings.TrailingStopPercent,
		},
		Technical: TechnicalParameters{
			RSIPeriod:                 config.TechIndicators.RSIPeriod,
			RSIOversold:               config.TechIndicators.RSIOversold,
			RSIOverbought:             config.TechIndicators.RSIOverbought,
			BBPeriod:                  config.TechIndicators.BBPeriod,
			BBStdDev:                  config.TechIndicators.BBStdDev,
			ATRPeriod:                 config.TechIndicators.ATRPeriod,
			ATRMultiplier:             config.TechIndicators.ATRMultiplier,
			AlphaTrendKalmanAlpha:     config.TechIndicators.AlphaTrendKalmanAlpha,
			AlphaTrendKalmanBeta:      config.TechIndicators.AlphaTrendKalmanBeta,
			AlphaTrendDeviation:       config.TechIndicators.AlphaTrendDeviation,
			AlphaTrendSupertrendFactor: config.TechIndicators.AlphaTrendSupertrendFactor,
			FastMAPeriod:              config.TechIndicators.FastMAPeriod,
			SlowMAPeriod:              config.TechIndicators.SlowMAPeriod,
			MAType:                    config.TechIndicators.MAType,
			VolumeAnalysisPeriod:      config.TechIndicators.VolumeAnalysisPeriod,
			VolumeSurgeMultiplier:     config.TechIndicators.VolumeMultiplier,
		},
		EntryExit: EntryExitParameters{
			MinSignalStrength:      config.EntryExit.MinSignalStrength,
			RequireVolumeConfirm:   config.EntryExit.RequireVolumeConfirm,
			RequireTrendAlignment:  config.EntryExit.RequireTrendAlignment,
			EntryDelayCandles:      config.EntryExit.EntryDelayCandles,
			ExitOnOppositeSignal:   config.EntryExit.ExitOnOppositeSignal,
			MaxHoldingPeriod:       config.EntryExit.MaxHoldingPeriod,
			MinProfitToClose:       config.EntryExit.MinProfitToClose,
			EnableTimeFilter:       config.EntryExit.EnableTimeFilter,
			TradingStartHour:       config.EntryExit.TradingStartHour,
			TradingEndHour:         config.EntryExit.TradingEndHour,
			EnableVolatilityFilter: config.EntryExit.EnableVolatilityFilter,
			MinVolatility:          config.EntryExit.MinVolatility,
			MaxVolatility:          config.EntryExit.MaxVolatility,
			VolatilityPeriod:       config.EntryExit.VolatilityPeriod,
			PositionSizing:         "FIXED", // Default for now
			Filters:                []string{}, // Build from flags
		},
	}

	// Build filters array
	if config.EntryExit.RequireVolumeConfirm {
		params.EntryExit.Filters = append(params.EntryExit.Filters, "VOLUME_CONFIRMATION")
	}
	if config.EntryExit.EnableTimeFilter {
		params.EntryExit.Filters = append(params.EntryExit.Filters, "TIME_FILTER")
	}
	if config.EntryExit.EnableVolatilityFilter {
		params.EntryExit.Filters = append(params.EntryExit.Filters, "VOLATILITY_FILTER")
	}

	// Cache the parsed parameters
	l.cache.Store(config.ID, params)

	return params, nil
}

// ClearCache removes all cached parameters
func (l *ParameterLoader) ClearCache() {
	l.cache = sync.Map{}
}

// RemoveFromCache removes specific config from cache
func (l *ParameterLoader) RemoveFromCache(configID uint) {
	l.cache.Delete(configID)
}

// SaveParameters saves parameters to a strategy configuration
func (l *ParameterLoader) SaveParameters(config *models.StrategyConfig, params *StrategyParameters) error {
	if config == nil {
		return fmt.Errorf("strategy config is nil")
	}
	if params == nil {
		return fmt.Errorf("parameters are nil")
	}

	// Update normalized structures
	if config.RiskSettings != nil {
		config.RiskSettings.MaxPositionSize = params.Risk.MaxPositionSize
		config.RiskSettings.RiskPerTrade = params.Risk.RiskPerTrade
		config.RiskSettings.StopLossPercent = params.Risk.StopLossPercent
		config.RiskSettings.TakeProfitPercent = params.Risk.TakeProfitPercent
		config.RiskSettings.StopLossATRMultiple = params.Risk.StopLossATRMultiple
		config.RiskSettings.TakeProfitATRMultiple = params.Risk.TakeProfitATRMultiple
		config.RiskSettings.MaxOpenPositions = params.Risk.MaxOpenPositions
		config.RiskSettings.MaxDrawdownLimit = params.Risk.MaxDrawdownLimit
		config.RiskSettings.DailyLossLimit = params.Risk.DailyLossLimit
		config.RiskSettings.UseTrailingStop = params.Risk.UseTrailingStop
		config.RiskSettings.TrailingStopPercent = params.Risk.TrailingStopPercent
	}

	if config.TechIndicators != nil {
		config.TechIndicators.RSIPeriod = params.Technical.RSIPeriod
		config.TechIndicators.RSIOversold = params.Technical.RSIOversold
		config.TechIndicators.RSIOverbought = params.Technical.RSIOverbought
		config.TechIndicators.BBPeriod = params.Technical.BBPeriod
		config.TechIndicators.BBStdDev = params.Technical.BBStdDev
		config.TechIndicators.ATRPeriod = params.Technical.ATRPeriod
		config.TechIndicators.ATRMultiplier = params.Technical.ATRMultiplier
		config.TechIndicators.AlphaTrendKalmanAlpha = params.Technical.AlphaTrendKalmanAlpha
		config.TechIndicators.AlphaTrendKalmanBeta = params.Technical.AlphaTrendKalmanBeta
		config.TechIndicators.AlphaTrendDeviation = params.Technical.AlphaTrendDeviation
		config.TechIndicators.AlphaTrendSupertrendFactor = params.Technical.AlphaTrendSupertrendFactor
		config.TechIndicators.FastMAPeriod = params.Technical.FastMAPeriod
		config.TechIndicators.SlowMAPeriod = params.Technical.SlowMAPeriod
		config.TechIndicators.MAType = params.Technical.MAType
		config.TechIndicators.VolumeAnalysisPeriod = params.Technical.VolumeAnalysisPeriod
		config.TechIndicators.VolumeMultiplier = params.Technical.VolumeSurgeMultiplier
	}

	if config.EntryExit != nil {
		config.EntryExit.MinSignalStrength = params.EntryExit.MinSignalStrength
		config.EntryExit.RequireVolumeConfirm = params.HasFilter("VOLUME_CONFIRMATION")
		config.EntryExit.RequireTrendAlignment = params.EntryExit.RequireTrendAlignment
		config.EntryExit.EntryDelayCandles = params.EntryExit.EntryDelayCandles
		config.EntryExit.ExitOnOppositeSignal = params.EntryExit.ExitOnOppositeSignal
		config.EntryExit.MaxHoldingPeriod = params.EntryExit.MaxHoldingPeriod
		config.EntryExit.MinProfitToClose = params.EntryExit.MinProfitToClose
		config.EntryExit.EnableTimeFilter = params.HasFilter("TIME_FILTER")
		config.EntryExit.TradingStartHour = params.EntryExit.TradingStartHour
		config.EntryExit.TradingEndHour = params.EntryExit.TradingEndHour
		config.EntryExit.EnableVolatilityFilter = params.HasFilter("VOLATILITY_FILTER")
		config.EntryExit.MinVolatility = params.EntryExit.MinVolatility
		config.EntryExit.MaxVolatility = params.EntryExit.MaxVolatility
		config.EntryExit.VolatilityPeriod = params.EntryExit.VolatilityPeriod
	}

	// Update cache
	l.cache.Store(config.ID, params)

	return nil
}

// ConvertGlobalConfigToParameters converts global config to strategy parameters
// This is used as a fallback or for initial strategy creation
