package strategy

import (
	"fmt"
	"os"
	"time"

	"bigmeanie/internal/types"
	"github.com/shopspring/decimal"
	"gopkg.in/yaml.v3"
)

// Config implements strategy-related configuration
type Config struct {
	TradingPairs                  []string
	RunMode                       string
	RSIPeriod                     int
	RSIOversold                   float64
	RSIOverbought                 float64
	BaseVolatility                decimal.Decimal
	VolatilityThreshold           decimal.Decimal
	HighVolatilityReduction       decimal.Decimal
	VolatilityMeasurementPeriod   int
	AlphaTrendKalmanAlpha         decimal.Decimal
	AlphaTrendKalmanBeta          decimal.Decimal
	AlphaTrendKalmanPeriod        int
	AlphaTrendDeviation           decimal.Decimal
	AlphaTrendSupertrendFactor    decimal.Decimal
	AlphaTrendSupertrendATRPeriod int
	VolumeMAShort                 int
	VolumeMALong                  int
	VolumeSurgeThreshold          decimal.Decimal
	VolumeExhaustionThreshold     decimal.Decimal
	VolumeTrendChangeThreshold    decimal.Decimal
	VROCPeriod                    int
	VolumeConfirmationWeight      decimal.Decimal
	EnableVolumeFilter            bool
	VolumeDivergencePeriod        int
	VolumeMultiplier              decimal.Decimal
	MinTimeBetweenTrades          time.Duration
	MarketDataStaleness           time.Duration
	SignalCooldownPeriod          time.Duration
	AnalysisCacheTTL              time.Duration
	MaxSignalConfirmations        int
	MediumSignalThreshold         int
	WeakSignalThreshold           int
	WeakSignalPenalty             decimal.Decimal
	Backtesting                   types.BacktestConfig
}

// yamlConfig is the structure for parsing YAML
type strategyYamlConfig struct {
	Strategy struct {
		TradingPairs []string `yaml:"trading_pairs"`
		RSI          struct {
			Period     int     `yaml:"period"`
			Oversold   float64 `yaml:"oversold"`
			Overbought float64 `yaml:"overbought"`
		} `yaml:"rsi"`
		Volatility struct {
			BaseVolatility          float64 `yaml:"base_volatility"`
			Threshold               float64 `yaml:"threshold"`
			HighVolatilityReduction float64 `yaml:"high_volatility_reduction"`
			MeasurementPeriod       int     `yaml:"measurement_period"`
		} `yaml:"volatility"`
		AlphaTrend struct {
			KalmanAlpha         float64 `yaml:"kalman_alpha"`
			KalmanBeta          float64 `yaml:"kalman_beta"`
			KalmanPeriod        int     `yaml:"kalman_period"`
			Deviation           float64 `yaml:"deviation"`
			SupertrendFactor    float64 `yaml:"supertrend_factor"`
			SupertrendATRPeriod int     `yaml:"supertrend_atr_period"`
		} `yaml:"alpha_trend"`
		Volume struct {
			MAShort                int     `yaml:"ma_short"`
			MALong                 int     `yaml:"ma_long"`
			SurgeThreshold         float64 `yaml:"surge_threshold"`
			ExhaustionThreshold    float64 `yaml:"exhaustion_threshold"`
			TrendChangeThreshold   float64 `yaml:"trend_change_threshold"`
			VROCPeriod             int     `yaml:"vroc_period"`
			ConfirmationWeight     float64 `yaml:"confirmation_weight"`
			EnableFilter           bool    `yaml:"enable_filter"`
			DivergencePeriod       int     `yaml:"divergence_period"`
			Multiplier             float64 `yaml:"multiplier"`
		} `yaml:"volume"`
		Timing struct {
			MinTimeBetweenTrades string `yaml:"min_time_between_trades"`
			MarketDataStaleness  string `yaml:"market_data_staleness"`
			SignalCooldownPeriod string `yaml:"signal_cooldown_period"`
			AnalysisCacheTTL     string `yaml:"analysis_cache_ttl"`
		} `yaml:"timing"`
		Signal struct {
			MaxConfirmations  int     `yaml:"max_confirmations"`
			MediumThreshold   int     `yaml:"medium_threshold"`
			WeakThreshold     int     `yaml:"weak_threshold"`
			WeakPenalty       float64 `yaml:"weak_penalty"`
		} `yaml:"signal"`
		Backtesting types.BacktestConfig `yaml:"backtesting"`
	} `yaml:"strategy"`
}

// LoadConfig loads strategy configuration from YAML and environment
func LoadConfig() (*Config, error) {
	// Load YAML config
	configFile := "config/strategy.yaml"
	if envFile := os.Getenv("STRATEGY_CONFIG_FILE"); envFile != "" {
		configFile = envFile
	}

	data, err := os.ReadFile(configFile)
	if err != nil {
		return nil, fmt.Errorf("failed to read strategy config: %w", err)
	}

	var yamlCfg strategyYamlConfig
	if err := yaml.Unmarshal(data, &yamlCfg); err != nil {
		return nil, fmt.Errorf("failed to parse strategy config: %w", err)
	}

	// Parse durations
	minTimeBetweenTrades, err := time.ParseDuration(yamlCfg.Strategy.Timing.MinTimeBetweenTrades)
	if err != nil {
		return nil, fmt.Errorf("invalid min_time_between_trades: %w", err)
	}

	marketDataStaleness, err := time.ParseDuration(yamlCfg.Strategy.Timing.MarketDataStaleness)
	if err != nil {
		return nil, fmt.Errorf("invalid market_data_staleness: %w", err)
	}

	signalCooldownPeriod, err := time.ParseDuration(yamlCfg.Strategy.Timing.SignalCooldownPeriod)
	if err != nil {
		return nil, fmt.Errorf("invalid signal_cooldown_period: %w", err)
	}

	analysisCacheTTL, err := time.ParseDuration(yamlCfg.Strategy.Timing.AnalysisCacheTTL)
	if err != nil {
		return nil, fmt.Errorf("invalid analysis_cache_ttl: %w", err)
	}

	// Build config
	cfg := &Config{
		TradingPairs:                  yamlCfg.Strategy.TradingPairs,
		RunMode:                       os.Getenv("RUN_MODE"), // from environment
		RSIPeriod:                     yamlCfg.Strategy.RSI.Period,
		RSIOversold:                   yamlCfg.Strategy.RSI.Oversold,
		RSIOverbought:                 yamlCfg.Strategy.RSI.Overbought,
		BaseVolatility:                decimal.NewFromFloat(yamlCfg.Strategy.Volatility.BaseVolatility),
		VolatilityThreshold:           decimal.NewFromFloat(yamlCfg.Strategy.Volatility.Threshold),
		HighVolatilityReduction:       decimal.NewFromFloat(yamlCfg.Strategy.Volatility.HighVolatilityReduction),
		VolatilityMeasurementPeriod:   yamlCfg.Strategy.Volatility.MeasurementPeriod,
		AlphaTrendKalmanAlpha:         decimal.NewFromFloat(yamlCfg.Strategy.AlphaTrend.KalmanAlpha),
		AlphaTrendKalmanBeta:          decimal.NewFromFloat(yamlCfg.Strategy.AlphaTrend.KalmanBeta),
		AlphaTrendKalmanPeriod:        yamlCfg.Strategy.AlphaTrend.KalmanPeriod,
		AlphaTrendDeviation:           decimal.NewFromFloat(yamlCfg.Strategy.AlphaTrend.Deviation),
		AlphaTrendSupertrendFactor:    decimal.NewFromFloat(yamlCfg.Strategy.AlphaTrend.SupertrendFactor),
		AlphaTrendSupertrendATRPeriod: yamlCfg.Strategy.AlphaTrend.SupertrendATRPeriod,
		VolumeMAShort:                 yamlCfg.Strategy.Volume.MAShort,
		VolumeMALong:                  yamlCfg.Strategy.Volume.MALong,
		VolumeSurgeThreshold:          decimal.NewFromFloat(yamlCfg.Strategy.Volume.SurgeThreshold),
		VolumeExhaustionThreshold:     decimal.NewFromFloat(yamlCfg.Strategy.Volume.ExhaustionThreshold),
		VolumeTrendChangeThreshold:    decimal.NewFromFloat(yamlCfg.Strategy.Volume.TrendChangeThreshold),
		VROCPeriod:                    yamlCfg.Strategy.Volume.VROCPeriod,
		VolumeConfirmationWeight:      decimal.NewFromFloat(yamlCfg.Strategy.Volume.ConfirmationWeight),
		EnableVolumeFilter:            yamlCfg.Strategy.Volume.EnableFilter,
		VolumeDivergencePeriod:        yamlCfg.Strategy.Volume.DivergencePeriod,
		VolumeMultiplier:              decimal.NewFromFloat(yamlCfg.Strategy.Volume.Multiplier),
		MinTimeBetweenTrades:          minTimeBetweenTrades,
		MarketDataStaleness:           marketDataStaleness,
		SignalCooldownPeriod:          signalCooldownPeriod,
		AnalysisCacheTTL:              analysisCacheTTL,
		MaxSignalConfirmations:        yamlCfg.Strategy.Signal.MaxConfirmations,
		MediumSignalThreshold:         yamlCfg.Strategy.Signal.MediumThreshold,
		WeakSignalThreshold:           yamlCfg.Strategy.Signal.WeakThreshold,
		WeakSignalPenalty:             decimal.NewFromFloat(yamlCfg.Strategy.Signal.WeakPenalty),
		Backtesting:                   yamlCfg.Strategy.Backtesting,
	}

	// Default run mode to paper if not set
	if cfg.RunMode == "" {
		cfg.RunMode = "paper"
	}

	// Default volume multiplier to 2.0 if not set
	if cfg.VolumeMultiplier.IsZero() {
		cfg.VolumeMultiplier = decimal.NewFromFloat(2.0)
	}

	return cfg, nil
}

// Getter methods for interfaces
func (c *Config) GetTradingPairs() []string {
	return c.TradingPairs
}

func (c *Config) GetRunMode() string {
	return c.RunMode
}

func (c *Config) GetRSIPeriod() int {
	return c.RSIPeriod
}

func (c *Config) GetRSIOversold() decimal.Decimal {
	return decimal.NewFromFloat(c.RSIOversold)
}

func (c *Config) GetRSIOverbought() decimal.Decimal {
	return decimal.NewFromFloat(c.RSIOverbought)
}

func (c *Config) GetATRPeriod() int {
	return c.VolatilityMeasurementPeriod
}

func (c *Config) GetVolumeMAPeriod() int {
	return c.VolumeMAShort
}

func (c *Config) GetVolumeSurgeMultiplier() decimal.Decimal {
	return c.VolumeSurgeThreshold
}

func (c *Config) GetAlphaTrendKalmanAlpha() decimal.Decimal {
	return c.AlphaTrendKalmanAlpha
}

func (c *Config) GetAlphaTrendKalmanBeta() decimal.Decimal {
	return c.AlphaTrendKalmanBeta
}

func (c *Config) GetAlphaTrendKalmanPeriod() int {
	return c.AlphaTrendKalmanPeriod
}

func (c *Config) GetAlphaTrendDeviation() decimal.Decimal {
	return c.AlphaTrendDeviation
}

func (c *Config) GetAlphaTrendSupertrendFactor() decimal.Decimal {
	return c.AlphaTrendSupertrendFactor
}

func (c *Config) GetAlphaTrendSupertrendATRPeriod() int {
	return c.AlphaTrendSupertrendATRPeriod
}

func (c *Config) GetBaseVolatility() decimal.Decimal {
	return c.BaseVolatility
}

func (c *Config) GetMinTimeBetweenTrades() time.Duration {
	return c.MinTimeBetweenTrades
}

func (c *Config) GetMarketDataStaleness() time.Duration {
	return c.MarketDataStaleness
}

func (c *Config) GetSignalCooldownPeriod() time.Duration {
	return c.SignalCooldownPeriod
}

func (c *Config) GetAnalysisCacheTTL() time.Duration {
	return c.AnalysisCacheTTL
}

func (c *Config) GetVolatilityThreshold() decimal.Decimal {
	return c.VolatilityThreshold
}

func (c *Config) GetEnableVolumeFilter() bool {
	return c.EnableVolumeFilter
}

func (c *Config) GetMaxSignalConfirmations() int {
	return c.MaxSignalConfirmations
}

func (c *Config) GetWeakSignalThreshold() int {
	return c.WeakSignalThreshold
}

func (c *Config) GetBacktestingConfig() types.BacktestConfig {
	return c.Backtesting
}


// Methods to implement DataCollectionConfig interface
func (c *Config) GetHistoricalDataDays() int {
	return 30 // Default 30 days
}

func (c *Config) GetGranularities() []string {
	return []string{"ONE_MINUTE", "FIVE_MINUTE", "FIFTEEN_MINUTE"} // Default granularities
}

func (c *Config) GetDataCollectionEnabled() bool {
	return true // Always enabled for strategies
}

func (c *Config) GetLogLevel() string {
	return "info" // Default log level
}

func (c *Config) GetDataGranularity() string {
	return "ONE_MINUTE" // Default granularity
}

func (c *Config) GetHistoricalDataPeriods() int {
	return 1000 // Default periods
}

func (c *Config) GetMarketDataCredentials() (string, string) {
	return "", "" // Strategy config doesn't manage credentials
}

func (c *Config) IsBacktestMode() bool {
	return c.RunMode == "backtest"
}

