func (c *Config) GetAnalysisEnabled() bool {
	return true // Always enabled for strategies
}

func (c *Config) GetTechnicalIndicators() []string {
	return []string{"RSI", "ATR", "MACD", "BollingerBands", "EMA", "AlphaTrend"} // Default indicators
}

