// Config implements data collection configuration
type Config struct {
	HistoricalDataPeriods int
	DataGranularity       string
	RunMode               string
	MarketDataKeyID       string
	MarketDataSecret      string
	CoinbaseAPIURL        string
	CoinbaseWebSocketURL  string
}

// yamlConfig is the structure for parsing YAML
