	Data struct {
		HistoricalDataPeriods int    `yaml:"historical_data_periods"`
		DataGranularity       string `yaml:"data_granularity"`
	} `yaml:"data"`
}

// LoadConfig loads data configuration from YAML and environment
func LoadConfig() (*Config, error) {
	// Load YAML config
	configFile := "config/data.yaml"
	if envFile := os.Getenv("DATA_CONFIG_FILE"); envFile != "" {
		configFile = envFile
	}

	data, err := os.ReadFile(configFile)
	if err != nil {
		return nil, fmt.Errorf("failed to read data config: %w", err)
	}

	var yamlCfg dataYamlConfig
	if err := yaml.Unmarshal(data, &yamlCfg); err != nil {
		return nil, fmt.Errorf("failed to parse data config: %w", err)
	}

	// Build config with environment variables for credentials
	cfg := &Config{
		HistoricalDataPeriods: yamlCfg.Data.HistoricalDataPeriods,
		DataGranularity:       yamlCfg.Data.DataGranularity,
		RunMode:               os.Getenv("RUN_MODE"),
		MarketDataKeyID:       os.Getenv("COINBASE_MARKET_DATA_KEY_ID"),
		MarketDataSecret:      os.Getenv("COINBASE_MARKET_DATA_SECRET"),
		CoinbaseAPIURL:        os.Getenv("COINBASE_API_URL"),
		CoinbaseWebSocketURL:  os.Getenv("COINBASE_WEBSOCKET_URL"),
	}

	// Fall back to legacy environment variables if new ones not set
	if cfg.MarketDataKeyID == "" {
		cfg.MarketDataKeyID = os.Getenv("COINBASE_KEY_ID")
	}
	if cfg.MarketDataSecret == "" {
		cfg.MarketDataSecret = os.Getenv("COINBASE_SECRET")
	}

	// Default run mode to paper if not set
	if cfg.RunMode == "" {
		cfg.RunMode = "paper"
