package backtest

import (
	"fmt"
	"time"

	"bigmeanie/internal/data"
	"bigmeanie/internal/models"
	"bigmeanie/pkg/timeseries"
)

// BacktestTimeSeriesProvider is a specialized TimeSeriesProvider for backtesting
// It uses preloaded time series data instead of fetching from database/API
type BacktestTimeSeriesProvider struct {
	timeSeries map[string]*timeseries.TimeSeries
}

// NewBacktestTimeSeriesProvider creates a new backtest time series provider
func NewBacktestTimeSeriesProvider(timeSeries map[string]*timeseries.TimeSeries) data.TimeSeriesProvider {
	return &BacktestTimeSeriesProvider{
		timeSeries: timeSeries,
	}
}

// LoadTimeSeries loads time series data for a given time range
func (p *BacktestTimeSeriesProvider) LoadTimeSeries(symbol string, start, end time.Time, granularity string) (*timeseries.TimeSeries, error) {
	if ts, exists := p.timeSeries[symbol]; exists {
		return p.filterTimeRange(ts, start, end), nil
	}
	return nil, fmt.Errorf("no time series data for %s", symbol)
}

// LoadHistoricalData returns the preloaded time series for the symbol
func (p *BacktestTimeSeriesProvider) LoadHistoricalData(symbol string, periods int, granularity string) (*timeseries.TimeSeries, error) {
	if ts, exists := p.timeSeries[symbol]; exists {
		return ts, nil
	}
	return nil, fmt.Errorf("no time series data for %s", symbol)
}

// GetLatestCandle returns the most recent candle from preloaded data
func (p *BacktestTimeSeriesProvider) GetLatestCandle(symbol string, granularity string) (*models.Candle, error) {
	ts, exists := p.timeSeries[symbol]
	if !exists {
		return nil, fmt.Errorf("no time series data for %s", symbol)
	}

	if ts.Len() == 0 {
		return nil, fmt.Errorf("no candles found for %s", symbol)
	}

	// Get the last candle
	lastCandle := ts.GetCandle(ts.Len() - 1)
	if lastCandle == nil {
		return nil, fmt.Errorf("failed to get last candle for %s", symbol)
	}

	// Convert to models.Candle
	return &models.Candle{
		Timestamp: lastCandle.Timestamp,
		Open:      lastCandle.Open,
		High:      lastCandle.High,
		Low:       lastCandle.Low,
		Close:     lastCandle.Close,
		Volume:    lastCandle.Volume,
	}, nil
}

// PreloadTimeSeries adds or updates preloaded time series data
func (p *BacktestTimeSeriesProvider) PreloadTimeSeries(symbol string, ts *timeseries.TimeSeries) error {
	if ts == nil {
		return fmt.Errorf("cannot preload nil time series")
	}
	p.timeSeries[symbol] = ts
	return nil
}

// filterTimeRange creates a new time series with only candles in the specified range
func (p *BacktestTimeSeriesProvider) filterTimeRange(ts *timeseries.TimeSeries, start, end time.Time) *timeseries.TimeSeries {
	filtered := timeseries.NewTimeSeries(ts.Symbol())

	for i := 0; i < ts.Len(); i++ {
		candle := ts.GetCandle(i)
		if candle.Timestamp.After(start) && candle.Timestamp.Before(end) {
			filtered.AddCandle(*candle)
		}
	}

	return filtered
}

// Adapter for HistoricalDataCollector interface compatibility
type backtestDataCollectorAdapter struct {
	provider data.TimeSeriesProvider
}

// NewBacktestDataCollectorAdapter creates an adapter that implements HistoricalDataCollector
func NewBacktestDataCollectorAdapter(provider data.TimeSeriesProvider) *backtestDataCollectorAdapter {
	return &backtestDataCollectorAdapter{
		provider: provider,
	}
}

// CollectHistoricalCandles is a no-op for backtesting
func (a *backtestDataCollectorAdapter) CollectHistoricalCandles(symbol string, start, end time.Time, granularity string) error {
	// Not used in backtesting - data is pre-loaded
	return nil
}

// LoadHistoricalDataForStrategy delegates to the TimeSeriesProvider
func (a *backtestDataCollectorAdapter) LoadHistoricalDataForStrategy(symbol string, periods int, granularity string) (*timeseries.TimeSeries, error) {
	return a.provider.LoadHistoricalData(symbol, periods, granularity)
}

