package backtest

import (
	"context"
	"fmt"
	"sort"
	"time"

	"bigmeanie/internal/common"
	"bigmeanie/internal/database"
	"bigmeanie/internal/interfaces"
	"bigmeanie/internal/models"
	"bigmeanie/internal/portfolio"
	"bigmeanie/internal/strategy"
	"bigmeanie/internal/trading"
	"bigmeanie/pkg/clock"
	"bigmeanie/pkg/logger"
	"bigmeanie/pkg/timeseries"
	"bigmeanie/pkg/types"

	"github.com/shopspring/decimal"
)

// BacktestEngine handles historical strategy simulation
type BacktestEngine struct {
	tradingFees      *types.TradingFees
	strategyConfig   *models.StrategyConfig
	run              *models.BacktestRun
	startCapital     decimal.Decimal
	currentCapital   decimal.Decimal
	currentTime      time.Time
	timeSeries       map[string]*timeseries.TimeSeries
	strategies       map[string]strategy.Strategy

	// Components
	simulatedPortfolio portfolio.SimulatedPortfolio
	portfolioManager   *PortfolioManager
	volumeAnalyzer     *VolumeAnalyzer

	// Database interfaces
	instrumentDB     database.Instrument
	candleDB         database.Candle
	backtestDB       database.Backtest
	strategyDB       database.Strategy

	instrumentCache  interfaces.InstrumentCache
	clock            clock.Clock
	log              *logger.ZapLogger
}

// NewBacktestEngine creates a new backtesting engine with specific DB interfaces
func NewBacktestEngine(
	tradingFees *types.TradingFees,
	backtestConfig *BacktestConfig,
	instrumentDB database.Instrument,
	candleDB database.Candle,
	backtestDB database.Backtest,
	strategyDB database.Strategy,
	instrumentCache interfaces.InstrumentCache,
	clk clock.Clock,
) (*BacktestEngine, error) {
	// Create fee calculator for portfolio
	feeCalc := trading.NewFeeCalculator(tradingFees)

	// Create backtest run record
	run := &models.BacktestRun{
		StrategyConfigID: backtestConfig.StrategyConfigID,
		Name:             backtestConfig.Name,
		StartDate:        backtestConfig.StartDate,
		EndDate:          backtestConfig.EndDate,
		InitialCapital:   backtestConfig.StartCapital,
		Status:           "RUNNING",
		CreatedAt:        clk.Now(),
	}

	// Create simulated portfolio
	portfolioID := fmt.Sprintf("backtest_%d", run.ID)
	simPortfolio := portfolio.NewSimulatedPortfolio(portfolioID, backtestConfig.StartCapital, feeCalc)

	engine := &BacktestEngine{
		tradingFees:        tradingFees,
		startCapital:       backtestConfig.StartCapital,
		currentCapital:     backtestConfig.StartCapital,
		timeSeries:         make(map[string]*timeseries.TimeSeries),
		strategies:         make(map[string]strategy.Strategy),
		run:                run,
		simulatedPortfolio: simPortfolio,
		// Database interfaces
		instrumentDB:       instrumentDB,
		candleDB:           candleDB,
		backtestDB:         backtestDB,
		strategyDB:         strategyDB,
		instrumentCache:    instrumentCache,
		clock:              clk,
		log:                logger.Backtest(),
	}

	// Create components
	engine.portfolioManager = NewPortfolioManager(
		run.ID,
		backtestConfig.StartCapital,
		&engine.currentCapital,
		time.Hour*24, // Daily snapshots
	)

	// Get volume multiplier from config
	volumeMultiplier := backtestConfig.GetVolumeMultiplier()
	engine.volumeAnalyzer = NewVolumeAnalyzer(backtestConfig.UseVolumeFilter, volumeMultiplier)

	// Load strategy configuration
	strategyConfig, err := engine.strategyDB.GetStrategyConfigByID(backtestConfig.StrategyConfigID)
	if err != nil {
		return nil, fmt.Errorf("failed to load strategy config: %w", err)
	}
	engine.strategyConfig = strategyConfig

	return engine, nil
}

// LoadHistoricalData loads historical candle data for backtesting
func (e *BacktestEngine) LoadHistoricalData(symbols []string, granularity string) error {
	for _, symbol := range symbols {
		e.log.Infow("Loading historical data", 
			"symbol", symbol,
			"start", e.run.StartDate.Format("2006-01-02"),
			"end", e.run.EndDate.Format("2006-01-02"))

		// Get instrument ID
		instrument, err := e.instrumentDB.GetInstrument(symbol)
		if err != nil {
			return fmt.Errorf("failed to get instrument for %s: %w", symbol, err)
		}

		// Load candles from database
		// Use GetHistoricalCandles with period calculation
		periods := int(e.run.EndDate.Sub(e.run.StartDate).Hours() / 24) // Days between start and end
		candles, err := e.candleDB.GetHistoricalCandles(
			fmt.Sprintf("%d", instrument.ID),
			granularity,
			periods,
		)

		if err != nil {
			return fmt.Errorf("failed to load candles for %s: %w", symbol, err)
		}

		// Create time series
		ts := timeseries.NewTimeSeries(symbol)
		for _, candle := range candles {
			ts.AddCandle(timeseries.Candle{
				Timestamp: candle.Timestamp,
				Open:      candle.Open,
				High:      candle.High,
				Low:       candle.Low,
				Close:     candle.Close,
				Volume:    candle.Volume,
			})
		}

		e.timeSeries[symbol] = ts
		e.log.Infow("Loaded historical data",
			"symbol", symbol,
			"candles", ts.Len())
	}

	return nil
}

// Run executes the backtest simulation
func (e *BacktestEngine) Run(ctx context.Context, symbols []string, granularity string) (*models.BacktestResult, error) {
	e.log.Infow("Starting backtest",
		"run_id", e.run.ID,
		"symbols", symbols,
		"start", e.run.StartDate.Format("2006-01-02"),
		"end", e.run.EndDate.Format("2006-01-02"))

	// Initialize strategies for each symbol
	for _, symbol := range symbols {
		if err := e.initializeStrategy(symbol); err != nil {
			return nil, fmt.Errorf("failed to initialize strategy for %s: %w", symbol, err)
		}
	}

	// Get all unique timestamps from all time series
	timestamps := e.getAllTimestamps()

	// Process each timestamp
	for _, timestamp := range timestamps {
		select {
		case <-ctx.Done():
			return nil, ctx.Err()
		default:
		}

		e.currentTime = timestamp

		// Update current prices
		e.updateCurrentPrices(timestamp)

		// Take portfolio snapshot if needed
		if e.portfolioManager.ShouldTakeSnapshot(timestamp) {
			positions := e.getPortfolioPositions()
			e.portfolioManager.TakeSnapshot(timestamp, positions)
		}

		// Process each symbol
		for _, symbol := range symbols {
			timeSeries := e.timeSeries[symbol]

			// Find the candle index for this timestamp
			timeIndex := e.findTimeIndex(timeSeries, timestamp)
			if timeIndex < 0 {
				continue
			}

			// Feed data to strategy
			candle := timeSeries.GetCandle(timeIndex)
			if err := e.feedHistoricalDataToStrategy(symbol, candle); err != nil {
				e.log.LogError("Failed to feed data to strategy", err, "symbol", symbol)
				continue
			}

			// Generate signals
			signals := e.generateSignals(symbol, timeIndex, timeSeries)

			// Execute signals
			for _, signal := range signals {
				// Convert backtest signal to trading signal
				tradingSignal := &trading.TradingSignal{
					Symbol:    signal.Symbol,
					Action:    signal.Action,
					Size:      signal.Size,
					Price:     signal.Price,
					Reason:    signal.Reason,
					Timestamp: timestamp,
				}

				// Execute trade through portfolio
				if _, err := e.simulatedPortfolio.ExecuteTrade(tradingSignal, signal.Price); err != nil {
					e.log.LogError("Failed to execute trade", err,
						"symbol", signal.Symbol,
						"action", signal.Action)
				}

				// Update current capital
				e.currentCapital = e.simulatedPortfolio.GetBalance("USD")
			}
		}
	}

	// Close all positions at end
	e.closeAllPositions(e.currentTime)

	// Take final snapshot
	positions := e.getPortfolioPositions()
	e.portfolioManager.TakeSnapshot(e.currentTime, positions)

	// Finalize and build results
	return e.finalizeBacktest()
}

// Private helper methods

func (e *BacktestEngine) initializeStrategy(symbol string) error {
	// Create a time series provider for the backtest
	timeSeriesProvider := NewBacktestTimeSeriesProvider(e.timeSeries)

	// Create a data collector adapter for backward compatibility
	dataCollector := NewBacktestDataCollectorAdapter(timeSeriesProvider)

	// Create a nil trade feed (not used in backtesting)
	tradeFeed := strategy.NewNilTradeFeed()

	// Create a config adapter that provides both StrategyConfig and AnalysisConfig interfaces
	// For now, we'll create a minimal implementation
	cfgAdapter := &backtestStrategyConfig{
		strategyConfig: e.strategyConfig,
	}

	// Create the strategy with specific database interfaces
	strat := strategy.NewMeanReversionStrategy(
		symbol,
		cfgAdapter,
		dataCollector,
		tradeFeed,
		e.instrumentDB,
		e.strategyDB,
		e.strategyDB,
		e.candleDB,
	)

	// Initialize the strategy
	if err := strat.Initialize(context.Background()); err != nil {
		return fmt.Errorf("failed to initialize strategy for %s: %w", symbol, err)
	}

	// Initialize with the time series
	if ts, exists := e.timeSeries[symbol]; exists {
		strat.InitializeWithTimeSeries(ts)
	}

	e.strategies[symbol] = strat
	return nil
}

// GetTrades returns all trades executed during the backtest as unified Trade models
func (e *BacktestEngine) GetTrades() []*models.Trade {
	// Get portfolio trades and convert to unified Trade model
	portfolioTrades := e.simulatedPortfolio.GetTradeHistory()
	trades := make([]*models.Trade, 0, len(portfolioTrades))

	for i, pTrade := range portfolioTrades {
		// Generate a unique source ID for this backtest trade
		sourceID := fmt.Sprintf("backtest_%d_%d", e.run.ID, i+1)

		// Use standardized trade builder
		side := models.TradeSideBuy
		signalType := "BUY"
		if pTrade.Side == trading.OrderSideSell {
			side = models.TradeSideSell
			signalType = "SELL"
		}

		builder := models.NewTradeBuilder(models.TradeSourceBacktest, pTrade.Symbol, side).
			WithSourceID(sourceID).
			WithBacktestRunID(e.run.ID).
			WithEntry(pTrade.ExecutedAt, pTrade.Price).
			WithQuantity(pTrade.Size).
			WithFees(pTrade.Fee).
			WithSignalType(signalType) // Preserve original side as signal type

		// For backtest trades, entry and exit happen at the same time
		// This represents a completed round-trip trade
		builder = builder.AsCompletedTrade(pTrade.ExecutedAt, pTrade.Price)

		trade, err := builder.Build()
		if err != nil {
			e.log.Warnw("Failed to build trade", "error", err)
			continue
		}

		// Override PnL with portfolio-calculated value
		trade.PnL = &pTrade.PnL

		trades = append(trades, trade)
	}

	return trades
}

func (e *BacktestEngine) getAllTimestamps() []time.Time {
	timestampMap := make(map[time.Time]bool)

	for _, ts := range e.timeSeries {
		for i := 0; i < ts.Len(); i++ {
			candle := ts.GetCandle(i)
			if candle != nil {
				timestampMap[candle.Timestamp] = true
			}
		}
	}

	timestamps := make([]time.Time, 0, len(timestampMap))
	for ts := range timestampMap {
		timestamps = append(timestamps, ts)
	}

	sort.Slice(timestamps, func(i, j int) bool {
		return timestamps[i].Before(timestamps[j])
	})

	return timestamps
}

func (e *BacktestEngine) updateCurrentPrices(timestamp time.Time) {
	// Portfolio tracks its own prices through trades
	// This method is no longer needed with SimulatedPortfolio
}

func (e *BacktestEngine) findTimeIndex(ts *timeseries.TimeSeries, timestamp time.Time) int {
	for i := 0; i < ts.Len(); i++ {
		candle := ts.GetCandle(i)
		if candle != nil && candle.Timestamp.Equal(timestamp) {
			return i
		}
	}
	return -1
}

func (e *BacktestEngine) feedHistoricalDataToStrategy(symbol string, candle *timeseries.Candle) error {
	if strat, exists := e.strategies[symbol]; exists {
		if meanRevStrat, ok := strat.(*strategy.MeanReversionStrategy); ok {
			meanRevStrat.AddWebSocketCandle(*candle)
		}
	}
	return nil
}

func (e *BacktestEngine) generateSignals(symbol string, timeIndex int, timeSeries *timeseries.TimeSeries) []*TradingSignal {
	signals := make([]*TradingSignal, 0)

	// Get the strategy
	strat, exists := e.strategies[symbol]
	if !exists {
		return signals
	}

	// Execute strategy
	if err := strat.Execute(context.Background()); err != nil {
		e.log.LogError("Strategy execution failed", err, "symbol", symbol)
		return signals
	}

	// Check if strategy generated a signal
	// This is simplified - in practice we'd need a way to extract signals from the strategy
	// For now, we'll use basic logic based on indicators

	if timeIndex < 20 { // Need minimum data for indicators
		return signals
	}

	// Example signal generation (simplified)
	candle := timeSeries.GetCandle(timeIndex)
	if candle != nil {
		// Check for entry signal
		positions := e.simulatedPortfolio.GetPositions()
		position, hasPosition := positions[symbol]

		if !hasPosition || position.Size.IsZero() {
			// Check volume confirmation if enabled
			if e.volumeAnalyzer.CheckVolumeConfirmation(symbol, timeSeries, timeIndex, "ENTRY") {
				// Generate buy signal (simplified)
				signal := &TradingSignal{
					Symbol: symbol,
					Action: "BUY",
					Price:  candle.Close,
					Size:   e.calculatePositionSize(candle.Close),
					Reason: "Mean reversion signal",
				}
				signals = append(signals, signal)
			}
		} else {
			// Check for exit signal
			if e.shouldExitPortfolioPosition(position, candle.Close) {
				signal := &TradingSignal{
					Symbol: symbol,
					Action: "CLOSE",
					Price:  candle.Close,
					Size:   position.Size,
					Reason: "Exit condition met",
				}
				signals = append(signals, signal)
			}
		}
	}

	return signals
}

func (e *BacktestEngine) shouldExitPortfolioPosition(position *portfolio.PortfolioPosition, currentPrice decimal.Decimal) bool {
	// Simple exit logic - 2% stop loss, 3% take profit
	stopLoss := decimal.NewFromFloat(0.02)
	takeProfit := decimal.NewFromFloat(0.03)

	priceChange := currentPrice.Sub(position.AveragePrice).Div(position.AveragePrice)

	// Exit if loss exceeds stop loss or profit exceeds take profit
	return priceChange.LessThanOrEqual(stopLoss.Neg()) || priceChange.GreaterThanOrEqual(takeProfit)
}

func (e *BacktestEngine) calculatePositionSize(price decimal.Decimal) decimal.Decimal {
	// Simple position sizing - 10% of capital
	riskPercent := decimal.NewFromFloat(0.1)
	positionValue := e.currentCapital.Mul(riskPercent)

	if price.IsPositive() {
		return positionValue.Div(price)
	}

	return decimal.Zero
}

func (e *BacktestEngine) finalizeBacktest() (*models.BacktestResult, error) {
	// Update run status
	e.run.Status = "COMPLETED"
	e.run.CompletedAt = &e.currentTime

	// Get volume statistics
	volumeStats := e.volumeAnalyzer.GetVolumeStatistics()

	// Build results
	builder := NewResultsBuilder(
		e.run,
		e.GetTrades(),
		e.portfolioManager.GetSnapshots(),
		volumeStats,
		e.startCapital,
		e.currentCapital,
		e.backtestDB,
	)

	return builder.BuildResults()
}

// GetRunID returns the current backtest run ID
func (e *BacktestEngine) GetRunID() uint {
	if e.run != nil {
		return e.run.ID
	}
	return 0
}

// SetTimeSeries sets the time series data for a symbol
func (e *BacktestEngine) SetTimeSeries(symbol string, timeSeries *timeseries.TimeSeries) {
	e.timeSeries[symbol] = timeSeries
}

// Helper types for mocking


// closeAllPositions closes all open positions at the current market price
func (e *BacktestEngine) closeAllPositions(timestamp time.Time) {
	positions := e.simulatedPortfolio.GetPositions()
	for symbol, position := range positions {
		if position.Size.IsPositive() {
			// Get current price for the symbol
			var currentPrice decimal.Decimal
			if ts, exists := e.timeSeries[symbol]; exists {
				index := e.findTimeIndex(ts, timestamp)
				if index >= 0 {
					candle := ts.GetCandle(index)
					if candle != nil {
						currentPrice = candle.Close
					}
				}
			}

			if currentPrice.IsPositive() {
				// Create close signal
				tradingSignal := &trading.TradingSignal{
					Symbol:    symbol,
					Action:    "CLOSE",
					Size:      position.Size,
					Price:     currentPrice,
					Reason:    "Backtest end - closing all positions",
					Timestamp: timestamp,
				}

				// Execute close
				if _, err := e.simulatedPortfolio.ExecuteTrade(tradingSignal, currentPrice); err != nil {
					e.log.LogError("Failed to close position", err, "symbol", symbol)
				}
			}
		}
	}
}

// getPortfolioPositions converts portfolio positions to trading positions for snapshots
func (e *BacktestEngine) getPortfolioPositions() map[string]*trading.Position {
	positions := e.simulatedPortfolio.GetPositions()
	tradingPositions := make(map[string]*trading.Position)

	for symbol, pos := range positions {
		if pos.Size.IsPositive() {
			tradingPositions[symbol] = &trading.Position{
				Symbol:        symbol,
				Side:          trading.PositionSideLong, // SimulatedPortfolio only supports long positions
				Size:          pos.Size,
				AvgPrice:      pos.AvgPrice,
				CurrentPrice:  pos.AvgPrice, // Will be updated by updateCurrentPrices
				UnrealizedPnL: decimal.Zero,
				RealizedPnL:   decimal.Zero,
				OpenedAt:      e.currentTime, // We don't track entry time in portfolio
				UpdatedAt:     e.currentTime,
			}
		}
	}

	return tradingPositions
}

