package data

import (
	"fmt"
	"os"

	"gopkg.in/yaml.v3"
)

// Config implements data collection configuration
type Config struct {
	HistoricalDataPeriods int
	DataGranularity       string
	RunMode               string
	MarketDataKeyID       string
	MarketDataSecret      string
	CoinbaseAPIURL        string
	CoinbaseWebSocketURL  string
	TradingPairs          []string
}

// yamlConfig is the structure for parsing YAML
type dataYamlConfig struct {
	Data struct {
		HistoricalDataPeriods int    `yaml:"historical_data_periods"`
		DataGranularity       string `yaml:"data_granularity"`
	} `yaml:"data"`
}

// LoadConfig loads data configuration from YAML and environment
func LoadConfig() (*Config, error) {
	// Load YAML config
	configFile := "config/data.yaml"
	if envFile := os.Getenv("DATA_CONFIG_FILE"); envFile != "" {
		configFile = envFile
	}

	data, err := os.ReadFile(configFile)
	if err != nil {
		return nil, fmt.Errorf("failed to read data config: %w", err)
	}

	var yamlCfg dataYamlConfig
	if err := yaml.Unmarshal(data, &yamlCfg); err != nil {
		return nil, fmt.Errorf("failed to parse data config: %w", err)
	}

	// Build config with environment variables for credentials
	cfg := &Config{
		HistoricalDataPeriods: yamlCfg.Data.HistoricalDataPeriods,
		DataGranularity:       yamlCfg.Data.DataGranularity,
		RunMode:               os.Getenv("RUN_MODE"),
		MarketDataKeyID:       os.Getenv("COINBASE_MARKET_DATA_KEY_ID"),
		MarketDataSecret:      os.Getenv("COINBASE_MARKET_DATA_SECRET"),
		CoinbaseAPIURL:        os.Getenv("COINBASE_API_URL"),
		CoinbaseWebSocketURL:  os.Getenv("COINBASE_WEBSOCKET_URL"),
	}

	// Fall back to legacy environment variables if new ones not set
	if cfg.MarketDataKeyID == "" {
		cfg.MarketDataKeyID = os.Getenv("COINBASE_KEY_ID")
	}
	if cfg.MarketDataSecret == "" {
		cfg.MarketDataSecret = os.Getenv("COINBASE_SECRET")
	}

	// Default run mode to paper if not set
	if cfg.RunMode == "" {
		cfg.RunMode = "paper"
	}

	return cfg, nil
}

// Getter methods
func (c *Config) GetHistoricalDataPeriods() int {
	if c.HistoricalDataPeriods == 0 {
		return 500 // default
	}
	return c.HistoricalDataPeriods
}

func (c *Config) GetDataGranularity() string {
	if c.DataGranularity == "" {
		return "FIVE_MINUTE" // default
	}
	return c.DataGranularity
}

func (c *Config) GetRunMode() string {
	return c.RunMode
}

func (c *Config) IsBacktestMode() bool {
	return c.RunMode == "backtest"
}

// DataCollectionConfig interface methods
func (c *Config) GetMarketDataCredentials() (keyID, secret string) {
	return c.MarketDataKeyID, c.MarketDataSecret
}

func (c *Config) IsPaperMode() bool {
	return c.RunMode == "paper"
}

func (c *Config) GetTradingPairs() []string {
	return c.TradingPairs
}

// APIConfig interface methods
func (c *Config) GetCoinbaseAPIURL() string {
	if c.CoinbaseAPIURL == "" {
		return "https://api.coinbase.com" // default
	}
	return c.CoinbaseAPIURL
}

func (c *Config) GetCoinbaseWebSocketURL() string {
	if c.CoinbaseWebSocketURL == "" {
		return "wss://advanced-trade-ws.coinbase.com" // default
	}
	return c.CoinbaseWebSocketURL
}

