package testhelper

import (
	"fmt"

	"bigmeanie/internal/backtest"
	"bigmeanie/internal/config"
	"bigmeanie/internal/data"
	"bigmeanie/internal/database"
	"bigmeanie/internal/strategy"
	"bigmeanie/internal/trading"
	"bigmeanie/pkg/logger"

	"gorm.io/gorm"
)

// TestDatabase wraps database connections and provides interfaces for testing
type TestDatabase struct {
	db               *gorm.DB
	InstrumentStore  database.Instrument
	CandleStore      database.Candle
	OrderStore       database.Order
	BacktestStore    database.Backtest
	StrategyStore    database.Strategy
	TradeStore       database.Trade
	VolumeStore      database.Volume
	TradingStore     database.Trading
	AnalysisStore    database.Analysis
}

// NewTestDatabase creates a new test database with all stores initialized
func NewTestDatabase(cfg *config.DatabaseConfig) (*TestDatabase, error) {
	// Connect to database
	db, err := database.NewProvider(cfg)
	if err != nil {
		return nil, fmt.Errorf("failed to connect to database: %w", err)
	}

	// Create stores
	instrumentStore := data.NewInstrumentStore(db)
	candleStore := data.NewCandleStore(db)
	orderStore := trading.NewOrderStore(db)
	backtestStore := backtest.NewBacktestStore(db)
	strategyStore := strategy.NewStrategyStore(db)
	tradeStore := trading.NewTradeStore(db)
	volumeStore := data.NewVolumeStore(db)
	tradingStore := trading.NewTradingStore(db)
	analysisStore := strategy.NewAnalysisStore(db)

	logger.Info("testhelper", "Database connected with all stores initialized")

	return &TestDatabase{
		db:               db,
		InstrumentStore:  instrumentStore,
		CandleStore:      candleStore,
		OrderStore:       orderStore,
		BacktestStore:    backtestStore,
		StrategyStore:    strategyStore,
		TradeStore:       tradeStore,
		VolumeStore:      volumeStore,
		TradingStore:     tradingStore,
		AnalysisStore:    analysisStore,
	}, nil
}

// DB returns the underlying GORM database connection
func (td *TestDatabase) DB() *gorm.DB {
	return td.db
}

// Close closes the database connection
func (td *TestDatabase) Close() error {
	sqlDB, err := td.db.DB()
	if err != nil {
		return err
	}
	return sqlDB.Close()
}

// SetGlobalDB sets the global database.DB variable for backward compatibility
// This should only be used temporarily while migrating legacy code
func (td *TestDatabase) SetGlobalDB() {
	database.DB = td.db
	logger.Warn("testhelper", "Global database.DB set - this is deprecated and should be avoided")
}

// Transaction executes a function within a database transaction
func (td *TestDatabase) Transaction(fn func(tx *TestDatabase) error) error {
	return td.db.Transaction(func(tx *gorm.DB) error {
		// Create a new TestDatabase with transaction-scoped stores
		txDB := &TestDatabase{
			db:               tx,
			InstrumentStore:  data.NewInstrumentStore(tx),
			CandleStore:      data.NewCandleStore(tx),
			OrderStore:       trading.NewOrderStore(tx),
			BacktestStore:    backtest.NewBacktestStore(tx),
			StrategyStore:    strategy.NewStrategyStore(tx),
			TradeStore:       trading.NewTradeStore(tx),
			VolumeStore:      data.NewVolumeStore(tx),
			TradingStore:     trading.NewTradingStore(tx),
			AnalysisStore:    strategy.NewAnalysisStore(tx),
		}
		return fn(txDB)
	})
}

