		log.Fatalf("Failed to connect to database: %v", err)
	}

	// Get underlying SQL database to ensure connection is established and defer closing
	sqlDB, err := db.DB()
	if err != nil {
		log.Fatalf("Failed to get underlying SQL database: %v", err)
	}
	defer sqlDB.Close()

	// Temporarily set the global DB variable for compatibility with existing code
	database.DB = db

	// Note: Database migrations should be run separately using a proper migration tool
	logger.Info("test", "Database migrations should be run separately")

	// Note: Initial data seeding should be done separately
	logger.Info("test", "Initial data seeding should be done separately")

	// Test basic operations
	if err := testBasicOperations(); err != nil {
		log.Fatalf("Basic operations test failed: %v", err)
	}

	// Test strategy configuration
	if err := testStrategyConfiguration(); err != nil {
		log.Fatalf("Strategy configuration test failed: %v", err)
	}

	// Test backtesting workflow
	if err := testBacktestingWorkflow(); err != nil {
		log.Fatalf("Backtesting workflow test failed: %v", err)
	}

	logger.Info("test", "All database tests passed successfully!")
}

func testBasicOperations() error {
	logger.Info("test", "Testing basic database operations...")

	// Test instrument creation and retrieval
	var instruments []models.Instrument
	if err := database.DB.Preload("Exchange").Find(&instruments).Error; err != nil {
		return fmt.Errorf("failed to fetch instruments: %w", err)
	}

	if len(instruments) < 2 {
		return fmt.Errorf("expected at least 2 instruments, got %d", len(instruments))
	}

	btcInstrument := instruments[0]
	logger.Info("test", "Found instrument: %s on %s", btcInstrument.Symbol, btcInstrument.Exchange.Name)

	// Test candle data insertion
	testCandle := models.Candle{
		InstrumentID: btcInstrument.ID,
		Granularity:  "ONE_HOUR",
		Timestamp:    time.Now().UTC(),
		Open:         decimal.NewFromFloat(45000.00),
		High:         decimal.NewFromFloat(45500.00),
		Low:          decimal.NewFromFloat(44800.00),
		Close:        decimal.NewFromFloat(45200.00),
		Volume:       decimal.NewFromFloat(1250.75),
	}

	if err := database.DB.Create(&testCandle).Error; err != nil {
		return fmt.Errorf("failed to create test candle: %w", err)
	}

	// Verify candle was created
	var retrievedCandle models.Candle
	if err := database.DB.Where("id = ?", testCandle.ID).First(&retrievedCandle).Error; err != nil {
		return fmt.Errorf("failed to retrieve test candle: %w", err)
	}

	logger.Info("test", "Successfully created and retrieved candle data")
	return nil
}

func testStrategyConfiguration() error {
	logger.Info("test", "Testing strategy configuration...")

	// Create a test strategy configuration
	riskConfig := `{
		"max_position_size": 0.03,
		"stop_loss_atr_multiple": 3.0,
		"daily_loss_limit": 0.05,
		"max_open_positions": 3
	}`

	technicalConfig := `{
		"rsi_period": 14,
		"rsi_oversold": 25,
		"rsi_overbought": 75,
		"alpha_trend_kalman_alpha": 0.05,
		"alpha_trend_deviation": 2.0
	}`

	entryExitConfig := `{
		"entry_signals": ["RSI_OVERSOLD", "TREND_FILTER_LOWER"],
		"exit_signals": ["RSI_OVERBOUGHT", "TREND_FILTER_UPPER", "STOP_LOSS"],
		"position_sizing": "FIXED_PERCENTAGE"
	}`

	tradingUniverse := `{
		"instruments": ["BTC-USD", "ETH-USD"],
		"market_cap_filter": "large_cap",
		"volume_filter": 1000000
	}`

	strategy := models.StrategyConfig{
		Name:                "Test RSI Mean Reversion",
		Version:             "1.0",
		Description:         "RSI-based mean reversion strategy for testing",
		StrategyType:        "MEAN_REVERSION",
		RiskManagement:      riskConfig,
		TechnicalIndicators: technicalConfig,
		EntryExitRules:      entryExitConfig,
		TradingUniverse:     tradingUniverse,
		CreatedBy:           "test_user",
		IsActive:            true,
	}

	if err := database.DB.Create(&strategy).Error; err != nil {
		return fmt.Errorf("failed to create strategy config: %w", err)
	}

	// Verify strategy was created and can be retrieved
	var retrievedStrategy models.StrategyConfig
	if err := database.DB.Where("id = ?", strategy.ID).First(&retrievedStrategy).Error; err != nil {
		return fmt.Errorf("failed to retrieve strategy config: %w", err)
	}

	logger.Info("test", "Successfully created strategy: %s (ID: %d)", retrievedStrategy.Name, retrievedStrategy.ID)
	return nil
}

func testBacktestingWorkflow() error {
	logger.Info("test", "Testing backtesting workflow...")

	// Get the strategy we created
	var strategy models.StrategyConfig
	if err := database.DB.Where("name = ?", "Test RSI Mean Reversion").First(&strategy).Error; err != nil {
		return fmt.Errorf("failed to find test strategy: %w", err)
	}

	// Get instruments for testing
	var instruments []models.Instrument
	if err := database.DB.Limit(2).Find(&instruments).Error; err != nil {
		return fmt.Errorf("failed to get instruments: %w", err)
	}

	// Create a backtest run
	backtest := models.BacktestRun{
		StrategyConfigID: strategy.ID,
		Name:             "Test Backtest Run",
		StartDate:        time.Now().AddDate(0, -1, 0), // 1 month ago
		EndDate:          time.Now(),
		InitialCapital:   decimal.NewFromFloat(10000.00),
		Instruments:      `["BTC-USD", "ETH-USD"]`,
		Status:           "COMPLETED",
		CompletedAt:      timePtr(time.Now()),
	}

	if err := database.DB.Create(&backtest).Error; err != nil {
		return fmt.Errorf("failed to create backtest run: %w", err)
	}

	// Create sample trades with composite keys
	trade1 := models.BacktestTrade{
		BacktestRunID: backtest.ID,
		TradeSequence: 1,
		InstrumentID:  instruments[0].ID,
		EntryTime:     time.Now().AddDate(0, 0, -5),
		ExitTime:      timePtr(time.Now().AddDate(0, 0, -3)),
		Side:          "LONG",
		EntryPrice:    decimal.NewFromFloat(45000.00),
		ExitPrice:     decimalPtr(decimal.NewFromFloat(46500.00)),
		Quantity:      decimal.NewFromFloat(0.1),
		PnL:           decimalPtr(decimal.NewFromFloat(150.00)),
		SignalType:    "RSI_OVERSOLD",
		Fees:          decimal.NewFromFloat(5.00),
	}

	trade2 := models.BacktestTrade{
		BacktestRunID: backtest.ID,
		TradeSequence: 2,
		InstrumentID:  instruments[1].ID,
		EntryTime:     time.Now().AddDate(0, 0, -4),
		ExitTime:      timePtr(time.Now().AddDate(0, 0, -2)),
		Side:          "LONG",
		EntryPrice:    decimal.NewFromFloat(3000.00),
		ExitPrice:     decimalPtr(decimal.NewFromFloat(2900.00)),
		Quantity:      decimal.NewFromFloat(1.0),
		PnL:           decimalPtr(decimal.NewFromFloat(-100.00)),
		SignalType:    "TREND_FILTER_LOWER",
		Fees:          decimal.NewFromFloat(3.00),
	}

	if err := database.DB.Create(&trade1).Error; err != nil {
		return fmt.Errorf("failed to create trade 1: %w", err)
	}

	if err := database.DB.Create(&trade2).Error; err != nil {
		return fmt.Errorf("failed to create trade 2: %w", err)
	}

	// Create backtest results
	results := models.BacktestResult{
		BacktestRunID:    backtest.ID,
		FinalCapital:     decimal.NewFromFloat(10042.00),
		TotalReturn:      decimal.NewFromFloat(0.0042),
		SharpeRatio:      decimal.NewFromFloat(0.85),
		MaxDrawdown:      decimal.NewFromFloat(-0.015),
		WinRate:          decimal.NewFromFloat(0.50),
		TotalTrades:      2,
		AvgTradeDuration: time.Hour * 48,
		ProfitFactor:     decimal.NewFromFloat(1.42),
		CalmarRatio:      decimal.NewFromFloat(0.28),
		SortinoRatio:     decimal.NewFromFloat(1.12),
	}

	if err := database.DB.Create(&results).Error; err != nil {
		return fmt.Errorf("failed to create backtest results: %w", err)
	}

	// Create portfolio snapshot with composite key
	snapshot := models.PortfolioSnapshot{
		BacktestRunID:       backtest.ID,
		SnapshotSequence:    1,
		Timestamp:           time.Now().AddDate(0, 0, -1),
		TotalValue:          decimal.NewFromFloat(10042.00),
		CashBalance:         decimal.NewFromFloat(8542.00),
		PositionsValue:      decimal.NewFromFloat(1500.00),
		UnrealizedPnL:       decimal.NewFromFloat(42.00),
		PortfolioBeta:       decimal.NewFromFloat(0.85),
		CorrelationToMarket: decimal.NewFromFloat(0.72),
		CurrentDrawdown:     decimal.NewFromFloat(-0.005),
		VaR95:               decimal.NewFromFloat(-250.00),
		LongExposure:        decimal.NewFromFloat(1500.00),
		ShortExposure:       decimal.NewFromFloat(0.00),
		NetExposure:         decimal.NewFromFloat(1500.00),
	}

	if err := database.DB.Create(&snapshot).Error; err != nil {
		return fmt.Errorf("failed to create portfolio snapshot: %w", err)
	}

	// Test complex queries
	if err := testComplexQueries(backtest.ID); err != nil {
		return fmt.Errorf("complex queries test failed: %w", err)
	}

	logger.Info("test", "Successfully completed backtesting workflow test")
	return nil
}

func testComplexQueries(backtestID uint) error {
	logger.Info("test", "Testing complex queries...")

	// Test 1: Get all trades with instruments and analysis
	var trades []models.BacktestTrade
	if err := database.DB.Preload("Instrument").Preload("Analysis").
		Where("backtest_run_id = ?", backtestID).Find(&trades).Error; err != nil {
		return fmt.Errorf("failed to query trades with preloads: %w", err)
	}

	logger.Info("test", "Found %d trades for backtest %d", len(trades), backtestID)

	// Test 2: Get backtest run with all related data
	var backtestRun models.BacktestRun
	if err := database.DB.Preload("StrategyConfig").
		Preload("Trades").Preload("Trades.Instrument").
		Preload("Results").Preload("PortfolioSnapshots").
		Where("id = ?", backtestID).First(&backtestRun).Error; err != nil {
		return fmt.Errorf("failed to query backtest run with all relations: %w", err)
	}

	logger.Info("test", "Backtest '%s' has %d trades and %d portfolio snapshots",
		backtestRun.Name, len(backtestRun.Trades), len(backtestRun.PortfolioSnapshots))

	// Test 3: Performance query - get profitable trades using composite key
	var profitableTrades []models.BacktestTrade
	if err := database.DB.Where("backtest_run_id = ? AND pnl > 0", backtestID).
		Find(&profitableTrades).Error; err != nil {
		return fmt.Errorf("failed to query profitable trades: %w", err)
	}

	logger.Info("test", "Found %d profitable trades", len(profitableTrades))

	// Test 4: Aggregation query - total PnL using composite key
	var totalPnL decimal.Decimal
	if err := database.DB.Model(&models.BacktestTrade{}).
		Where("backtest_run_id = ? AND pnl IS NOT NULL", backtestID).
		Select("COALESCE(SUM(pnl), 0)").Scan(&totalPnL).Error; err != nil {
		return fmt.Errorf("failed to calculate total PnL: %w", err)
	}

	logger.Info("test", "Total PnL for backtest: %s", totalPnL.String())

	return nil
}

// Helper functions
func timePtr(t time.Time) *time.Time {
	return &t
}

func decimalPtr(d decimal.Decimal) *decimal.Decimal {
	return &d
}

