//go:build ignore
// +build ignore

package main

import (
	"fmt"
	"log"
	"time"

	"bigmeanie/internal/backtest"
	"bigmeanie/internal/config"
	"bigmeanie/internal/database"
	"bigmeanie/internal/models"
	"bigmeanie/pkg/metrics"

	"github.com/shopspring/decimal"
)

type backtestTester struct {
	cfg            *config.GlobalConfig
	backtestConfig *backtest.BacktestConfig
}

func main() {
	fmt.Println("=== Backtesting Engine Test ===")

	// Initialize test environment
	tester := initializeBacktestTester()

	// Create and test engine
	tester.createBacktestEngine()

	fmt.Println("\n--- Testing Engine Components ---")

	// Test performance metrics
	tester.testPerformanceMetrics()

	// Display completion summary
	displayCompletionSummary()
}

// initializeBacktestTester sets up the test environment
func initializeBacktestTester() *backtestTester {
	cfg := loadConfiguration()
	connectToDatabase(cfg)
	backtestConfig := createBacktestConfig()

	return &backtestTester{
		cfg:            cfg,
		backtestConfig: backtestConfig,
	}
}

// loadConfiguration loads the system configuration
func loadConfiguration() *config.GlobalConfig {
	cfg, err := config.LoadGlobalConfig()
	if err != nil {
		log.Fatalf("Failed to load config: %v", err)
	}
	return cfg
}

// connectToDatabase establishes database connection
func connectToDatabase(cfg *config.GlobalConfig) {
	db, err := database.NewProvider(cfg.Database)
	if err != nil {
		log.Fatalf("Failed to connect to database: %v", err)
	}
	// Set global DB for compatibility
	database.DB = db
	fmt.Println("âœ… Database connected successfully")
}

// createBacktestConfig creates the backtesting configuration
func createBacktestConfig() *backtest.BacktestConfig {
	backtestConfig := &backtest.BacktestConfig{
		StrategyConfigID: 1, // Assume strategy config exists
		StartDate:        time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC),
		EndDate:          time.Date(2024, 1, 31, 0, 0, 0, 0, time.UTC),
		StartCapital:     decimal.NewFromFloat(10000), // $10,000 starting capital
		Symbols:          []string{"BTC-USD"},
		Granularity:      "ONE_HOUR",
		Name:             "Test Backtesting Run",
		Description:      "Testing backtesting engine with performance metrics",
	}

	fmt.Printf("âœ… Backtest config created: %s to %s with $%.2f starting capital\n",
		backtestConfig.StartDate.Format("2006-01-02"),
		backtestConfig.EndDate.Format("2006-01-02"),
		backtestConfig.StartCapital.InexactFloat64())

	return backtestConfig
}

// createBacktestEngine creates the backtesting engine
func (t *backtestTester) createBacktestEngine() {
	_, err := backtest.NewBacktestEngine(t.cfg, t.backtestConfig)
	if err != nil {
		log.Fatalf("Failed to create backtest engine: %v", err)
	}
	fmt.Println("âœ… Backtesting engine created successfully")
}

// testPerformanceMetrics tests performance metrics calculation
func (t *backtestTester) testPerformanceMetrics() {
	fmt.Println("Testing performance metrics calculation...")

	// Create sample data
	sampleSnapshots := createSampleSnapshots()
	sampleTrades := createSampleTrades()

	// Calculate metrics
	calc := metrics.NewCalculator()
	metricsResult := calc.Calculate(
		sampleTrades,
		sampleSnapshots,
		decimal.NewFromFloat(10000), // start capital
		decimal.NewFromFloat(10200), // final capital
		time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC),
		time.Date(2024, 1, 4, 0, 0, 0, 0, time.UTC),
	)

	// Display metrics
	displayPerformanceMetrics(metricsResult)
	displayRiskAdjustedMetrics(metricsResult)
	displayTradeStatistics(metricsResult)
}

// createSampleSnapshots creates sample portfolio snapshots
func createSampleSnapshots() []*models.PortfolioSnapshot {
	return []*models.PortfolioSnapshot{
		{
			BacktestRunID:    1,
			SnapshotSequence: 1,
			Timestamp:        time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC),
			TotalValue:       decimal.NewFromFloat(10000),
		},
		{
			BacktestRunID:    1,
			SnapshotSequence: 2,
			Timestamp:        time.Date(2024, 1, 2, 0, 0, 0, 0, time.UTC),
			TotalValue:       decimal.NewFromFloat(10100),
		},
		{
			BacktestRunID:    1,
			SnapshotSequence: 3,
			Timestamp:        time.Date(2024, 1, 3, 0, 0, 0, 0, time.UTC),
			TotalValue:       decimal.NewFromFloat(9950),
		},
		{
			BacktestRunID:    1,
			SnapshotSequence: 4,
			Timestamp:        time.Date(2024, 1, 4, 0, 0, 0, 0, time.UTC),
			TotalValue:       decimal.NewFromFloat(10200),
		},
	}
}

// createSampleTrades creates sample trades
func createSampleTrades() []*models.BacktestTrade {
	exitTime1 := time.Date(2024, 1, 1, 2, 0, 0, 0, time.UTC)
	exitPrice1 := decimal.NewFromFloat(50100)
	pnl1 := decimal.NewFromFloat(100)

	exitTime2 := time.Date(2024, 1, 2, 1, 0, 0, 0, time.UTC)
	exitPrice2 := decimal.NewFromFloat(50050)
	pnl2 := decimal.NewFromFloat(50)

	exitTime3 := time.Date(2024, 1, 3, 3, 0, 0, 0, time.UTC)
	exitPrice3 := decimal.NewFromFloat(49800)
	pnl3 := decimal.NewFromFloat(-100)

	return []*models.BacktestTrade{
		{
			BacktestRunID: 1,
			TradeSequence: 1,
			EntryTime:     time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC),
			ExitTime:      &exitTime1,
			Side:          "LONG",
			EntryPrice:    decimal.NewFromFloat(50000),
			ExitPrice:     &exitPrice1,
			PnL:           &pnl1,
		},
		{
			BacktestRunID: 1,
			TradeSequence: 2,
			EntryTime:     time.Date(2024, 1, 2, 0, 0, 0, 0, time.UTC),
			ExitTime:      &exitTime2,
			Side:          "SHORT",
			EntryPrice:    decimal.NewFromFloat(50100),
			ExitPrice:     &exitPrice2,
			PnL:           &pnl2,
		},
		{
			BacktestRunID: 1,
			TradeSequence: 3,
			EntryTime:     time.Date(2024, 1, 3, 0, 0, 0, 0, time.UTC),
			ExitTime:      &exitTime3,
			Side:          "LONG",
			EntryPrice:    decimal.NewFromFloat(49900),
			ExitPrice:     &exitPrice3,
			PnL:           &pnl3,
		},
	}
}

// displayPerformanceMetrics shows calculated performance metrics
func displayPerformanceMetrics(metrics *metrics.PerformanceMetrics) {
	fmt.Printf("âœ… Performance Metrics Calculated:\n")
	fmt.Printf("   Total Return: %.2f%%\n", metrics.TotalReturnPercent.InexactFloat64())
	fmt.Printf("   Total Trades: %d\n", metrics.TotalTrades)
	fmt.Printf("   Winning Trades: %d\n", metrics.WinningTrades)
	fmt.Printf("   Losing Trades: %d\n", metrics.LosingTrades)
	fmt.Printf("   Win Rate: %.1f%%\n", metrics.WinRatePercent.InexactFloat64())
	fmt.Printf("   Profit Factor: %.2f\n", metrics.ProfitFactor.InexactFloat64())
	fmt.Printf("   Sharpe Ratio: %.3f\n", metrics.SharpeRatio.InexactFloat64())
	fmt.Printf("   Max Drawdown: %.2f%%\n", metrics.MaxDrawdownPercent.InexactFloat64())

	if !metrics.VolatilityAnnual.IsZero() {
		fmt.Printf("   Annual Volatility: %.2f%%\n", metrics.VolatilityAnnual.InexactFloat64())
	}
}

// displayRiskAdjustedMetrics shows risk-adjusted metrics
func displayRiskAdjustedMetrics(metrics *metrics.PerformanceMetrics) {
	fmt.Printf("\nâœ… Risk-Adjusted Metrics Summary:\n")
	fmt.Printf("   Sharpe Ratio: %.3f\n", metrics.SharpeRatio.InexactFloat64())
	fmt.Printf("   Sortino Ratio: %.3f\n", metrics.SortinoRatio.InexactFloat64())
	fmt.Printf("   Calmar Ratio: %.3f\n", metrics.CalmarRatio.InexactFloat64())
	fmt.Printf("   Max Drawdown: %.2f%%\n", metrics.MaxDrawdownPercent.InexactFloat64())
	fmt.Printf("   Volatility Annual: %.2f%%\n", metrics.VolatilityAnnual.InexactFloat64())
	fmt.Printf("   Win Rate: %.2f%%\n", metrics.WinRatePercent.InexactFloat64())
	fmt.Printf("   Profit Factor: %.3f\n", metrics.ProfitFactor.InexactFloat64())
	fmt.Printf("   Total Return: %.2f%%\n", metrics.TotalReturnPercent.InexactFloat64())
}

// displayTradeStatistics shows trade statistics
func displayTradeStatistics(metrics *metrics.PerformanceMetrics) {
	fmt.Printf("\nâœ… Trade Statistics Summary:\n")
	fmt.Printf("   Total Trades: %d\n", metrics.TotalTrades)
	fmt.Printf("   Winning Trades: %d\n", metrics.WinningTrades)
	fmt.Printf("   Losing Trades: %d\n", metrics.LosingTrades)
	fmt.Printf("   Win Rate: %.2f%%\n", metrics.WinRatePercent.InexactFloat64())
	fmt.Printf("   Avg Win: %.2f\n", metrics.AvgWin.InexactFloat64())
	fmt.Printf("   Avg Loss: %.2f\n", metrics.AvgLoss.InexactFloat64())
	fmt.Printf("   Largest Win: %.2f\n", metrics.LargestWin.InexactFloat64())
	fmt.Printf("   Largest Loss: %.2f\n", metrics.LargestLoss.InexactFloat64())
	fmt.Printf("   Profit Factor: %.2f\n", metrics.ProfitFactor.InexactFloat64())
	fmt.Printf("   Avg Trade Duration: %s\n", metrics.AvgTradeDuration.String())
	fmt.Printf("   Expectancy Ratio: %.2f\n", metrics.ExpectancyRatio.InexactFloat64())
}

// displayCompletionSummary shows completion summary
