	// Estimate runtime
	estimatedRuntime := strategies.EstimateRuntime(gridConfig.MaxCombinations, gridConfig.WorkerCount, 2.0)
	fmt.Printf("âœ… Estimated runtime: %.1f minutes\n", estimatedRuntime.Minutes())

	// Test 3: Random Search Optimization
	fmt.Println("\n--- Testing Random Search Optimization ---")

	randomConfig := &strategies.RandomSearchConfig{
		OptimizationTarget:     "SHARPE",
		MaxCombinations:        30, // Smaller for testing
		WorkerCount:            4,
		StartDate:              time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC),
