func testParameterSpace() {
	// Create a test optimization config
	optimizationConfig := &optimization.OptimizationConfig{
		BaseStrategyConfigID: 1,
		OptimizationType:     "GRID_SEARCH",
		OptimizationTarget:   "SHARPE",
		MaxCombinations:      100,
	}

	// Create parameter space
	parameterSpace, err := optimization.NewParameterSpace(optimizationConfig)
	if err != nil {
		log.Printf("Failed to create parameter space: %v", err)
		return
	}

	fmt.Printf("âœ… Parameter space created with %d parameters\n", len(parameterSpace.GetParameterInfo()))

	// Test parameter info
	paramInfo := parameterSpace.GetParameterInfo()
	for name, param := range paramInfo {
		fmt.Printf("   Parameter %s: type=%s, default=%v\n", name, param.Type, param.Default)
	}

	// Test combination generation
	combinations := parameterSpace.GenerateCombinations(10)
	fmt.Printf("âœ… Generated %d parameter combinations\n", len(combinations))

	for i, combination := range combinations {
		if i < 3 { // Show first 3 combinations
			fmt.Printf("   Combination %d: %v\n", combination.ID, combination.Parameters)
		}
	}

	// Test random combination generation
	randomCombinations := parameterSpace.GenerateRandomCombinations(5)
	fmt.Printf("âœ… Generated %d random combinations\n", len(randomCombinations))

	// Test default combination
	defaultCombination := parameterSpace.GetDefaultCombination()
	fmt.Printf("âœ… Default combination: %v\n", defaultCombination.Parameters)

	// Test parameter validation
	if err := parameterSpace.ValidateCombination(defaultCombination.Parameters); err != nil {
		log.Printf("Default combination validation failed: %v", err)
	} else {
		fmt.Println("âœ… Default combination validated successfully")
	}

	// Test parameter count
	totalCombinations := parameterSpace.GetParameterCount()
	fmt.Printf("âœ… Total possible combinations: %d\n", totalCombinations)

	// Test JSON serialization
	jsonData, err := parameterSpace.ToJSON()
	if err != nil {
		log.Printf("JSON serialization failed: %v", err)
	} else {
		fmt.Printf("âœ… Parameter space serialized to JSON (%d bytes)\n", len(jsonData))
	}
}

func testOptimizationEngine(cfg *config.Config) {
	// Create a minimal optimization config for testing
	optimizationConfig := &optimization.OptimizationConfig{
		BaseStrategyConfigID: 1,
		OptimizationType:     "TEST",
		OptimizationTarget:   "SHARPE",
		StartDate:            time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC),
		EndDate:              time.Date(2024, 1, 2, 0, 0, 0, 0, time.UTC),
		StartCapital:         decimal.NewFromFloat(10000),
		Symbols:              []string{"BTC-USD"},
		Granularity:          "ONE_HOUR",
		MaxCombinations:      5,
		WorkerCount:          2,
		Name:                 "Test Engine",
		Description:          "Testing optimization engine components",
	}

	// Create optimization engine
	engine, err := optimization.NewOptimizationEngine(cfg, optimizationConfig, database.DB)
	if err != nil {
		log.Printf("Failed to create optimization engine: %v", err)
		return
	}

	fmt.Println("âœ… Optimization engine created successfully")

	// Test progress callback
	progressCallbacks := 0
	engine.SetProgressCallback(func(completed, total int, bestMetric decimal.Decimal) {
		progressCallbacks++
		fmt.Printf("   Progress callback %d: %d/%d completed, best metric: %.3f\n",
			progressCallbacks, completed, total, bestMetric.InexactFloat64())
	})

	fmt.Println("âœ… Progress callback configured")

	// Test results tracker
	results := engine.GetResults()
	completed, total, bestMetric := results.GetProgress()
	fmt.Printf("âœ… Initial progress: %d/%d completed, best metric: %.3f\n",
		completed, total, bestMetric.InexactFloat64())

	// Note: We don't run the actual optimization here as it requires historical data
	fmt.Println("âœ… Optimization engine components validated")
}

func testSamplingStrategies() {
	// Test sampling strategy information
	strategies := strategies.GetSamplingStrategies()
	fmt.Printf("âœ… Available sampling strategies: %d\n", len(strategies))

	for strategy, description := range strategies {
		fmt.Printf("   %s: %s\n", strategy, description)
	}

	// Test Latin Hypercube vs Random sampling efficiency
	optimizationConfig := &optimization.OptimizationConfig{
		BaseStrategyConfigID: 1,
		OptimizationType:     "TEST",
		MaxCombinations:      20,
	}

	parameterSpace, err := optimization.NewParameterSpace(optimizationConfig)
	if err != nil {
		log.Printf("Failed to create parameter space for sampling test: %v", err)
		return
	}

	// Generate random combinations
	randomCombinations := parameterSpace.GenerateRandomCombinations(20)

	// Calculate uniqueness
	uniqueHashes := make(map[string]bool)
	for _, combination := range randomCombinations {
		uniqueHashes[combination.Hash] = true
	}

	efficiency := float64(len(uniqueHashes)) / float64(len(randomCombinations))
	fmt.Printf("âœ… Random sampling efficiency: %.1f%% unique combinations\n", efficiency*100)

	fmt.Println("âœ… Sampling strategies tested successfully")
}

