package metrics

import (
	"fmt"
	"math"
	"sort"
	"time"

	"bigmeanie/internal/models"
	"github.com/shopspring/decimal"
)

// Calculator calculates performance metrics from trades and portfolio snapshots
type Calculator interface {
	// Calculate computes all performance metrics
	Calculate(trades []*models.Trade, snapshots []*models.PortfolioSnapshot, startCapital, endCapital decimal.Decimal, startDate, endDate time.Time) *PerformanceMetrics

	// Individual metric calculations
	CalculateSharpeRatio(returns []decimal.Decimal, riskFreeRate decimal.Decimal) decimal.Decimal
	CalculateSortinoRatio(returns []decimal.Decimal, riskFreeRate decimal.Decimal, targetReturn decimal.Decimal) decimal.Decimal
	CalculateMaxDrawdown(values []decimal.Decimal) (maxDD decimal.Decimal, maxDDPercent decimal.Decimal)
	CalculateVolatility(returns []decimal.Decimal) decimal.Decimal
}

// DefaultCalculator is the default implementation of Calculator
type DefaultCalculator struct {
	annualizationFactor float64 // Default 252 for daily data
}

// NewCalculator creates a new metrics calculator
func NewCalculator() Calculator {
	return &DefaultCalculator{
		annualizationFactor: 252, // Trading days per year
	}
}

// NewCalculatorWithAnnualization creates a calculator with custom annualization factor
func NewCalculatorWithAnnualization(annualizationFactor float64) Calculator {
	return &DefaultCalculator{
		annualizationFactor: annualizationFactor,
	}
}

// Calculate computes all performance metrics
func (c *DefaultCalculator) Calculate(trades []*models.Trade, snapshots []*models.PortfolioSnapshot, startCapital, endCapital decimal.Decimal, startDate, endDate time.Time) *PerformanceMetrics {
	metrics := &PerformanceMetrics{}

	// Basic return metrics
	c.calculateReturnMetrics(metrics, startCapital, endCapital, startDate, endDate)

	// Trade statistics
	c.calculateTradeStatistics(metrics, trades)

	// Risk metrics from portfolio snapshots
	if len(snapshots) > 1 {
		returns := c.calculateReturns(snapshots)
		c.calculateRiskMetrics(metrics, returns, snapshots)
		c.calculateRiskAdjustedReturns(metrics, returns)
	}

	// Time-based metrics
	c.calculateTimeMetrics(metrics, trades, startDate, endDate)

	// Advanced metrics
	c.calculateAdvancedMetrics(metrics)

	return metrics
}

// CalculateFromBacktestTrades computes metrics from legacy BacktestTrade objects
func (c *DefaultCalculator) CalculateFromBacktestTrades(trades []*models.BacktestTrade, snapshots []*models.PortfolioSnapshot, startCapital, endCapital decimal.Decimal, startDate, endDate time.Time) *PerformanceMetrics {
	// Convert BacktestTrade to Trade for calculation
	unifiedTrades := make([]*models.Trade, 0, len(trades))
	for _, bt := range trades {
		// Get symbol from instrument if available
		symbol := fmt.Sprintf("INST_%d", bt.InstrumentID)
		trade := models.ConvertFromBacktestTrade(bt, symbol)
		unifiedTrades = append(unifiedTrades, trade)
	}

	// Use the main Calculate method
	return c.Calculate(unifiedTrades, snapshots, startCapital, endCapital, startDate, endDate)
}

// calculateReturnMetrics calculates basic return metrics
func (c *DefaultCalculator) calculateReturnMetrics(metrics *PerformanceMetrics, startCapital, endCapital decimal.Decimal, startDate, endDate time.Time) {
	if startCapital.IsZero() {
		return
	}

	// Total return
	totalReturn := endCapital.Sub(startCapital).Div(startCapital)
	metrics.TotalReturn = totalReturn
	metrics.TotalReturnPercent = totalReturn.Mul(decimal.NewFromInt(100))

	// CAGR (Compound Annual Growth Rate)
	years := endDate.Sub(startDate).Hours() / 24 / 365.25
	if years > 0 {
		// CAGR = (Ending Value / Beginning Value)^(1/years) - 1
		growthMultiple := endCapital.Div(startCapital)
		cagr := decimalPow(growthMultiple, decimal.NewFromFloat(1/years)).Sub(decimal.NewFromInt(1))
		metrics.CAGR = cagr.Mul(decimal.NewFromInt(100))
	}
}

// calculateTradeStatistics calculates trade-level statistics
func (c *DefaultCalculator) calculateTradeStatistics(metrics *PerformanceMetrics, trades []*models.Trade) {
	if len(trades) == 0 {
		return
	}

	var grossProfit, grossLoss decimal.Decimal
	var winPnLSum, lossPnLSum decimal.Decimal
	var currentWinStreak, currentLossStreak int
	var maxWinStreak, maxLossStreak int
	var closedTradeCount int

	for _, trade := range trades {
		// Skip open trades
		if trade.Status != models.TradeStatusClosed {
			continue
		}

		closedTradeCount++

		if trade.PnL == nil || trade.PnL.IsZero() {
			continue
		}

		if trade.PnL.IsPositive() {
			metrics.WinningTrades++
			grossProfit = grossProfit.Add(*trade.PnL)
			winPnLSum = winPnLSum.Add(*trade.PnL)

			currentWinStreak++
			currentLossStreak = 0
			if currentWinStreak > maxWinStreak {
				maxWinStreak = currentWinStreak
			}

			if trade.PnL.GreaterThan(metrics.LargestWin) {
				metrics.LargestWin = *trade.PnL
			}
		} else {
			metrics.LosingTrades++
			grossLoss = grossLoss.Add(trade.PnL.Abs())
			lossPnLSum = lossPnLSum.Add(*trade.PnL)

			currentLossStreak++
			currentWinStreak = 0
			if currentLossStreak > maxLossStreak {
				maxLossStreak = currentLossStreak
			}

			if trade.PnL.LessThan(metrics.LargestLoss) {
				metrics.LargestLoss = *trade.PnL
			}
		}
	}

	metrics.TotalTrades = closedTradeCount
	metrics.GrossProfit = grossProfit
	metrics.GrossLoss = grossLoss
	metrics.NetProfit = grossProfit.Sub(grossLoss)
	metrics.ConsecutiveWins = maxWinStreak
	metrics.ConsecutiveLosses = maxLossStreak
	metrics.CurrentWinStreak = currentWinStreak
	metrics.CurrentLossStreak = currentLossStreak

	// Calculate ratios and averages
	completedTrades := metrics.WinningTrades + metrics.LosingTrades
	if completedTrades > 0 {
		metrics.WinRate = decimal.NewFromInt(int64(metrics.WinningTrades)).Div(decimal.NewFromInt(int64(completedTrades)))
		metrics.WinRatePercent = metrics.WinRate.Mul(decimal.NewFromInt(100))
		metrics.AvgTrade = metrics.NetProfit.Div(decimal.NewFromInt(int64(completedTrades)))
	}

	if metrics.WinningTrades > 0 {
		metrics.AvgWin = winPnLSum.Div(decimal.NewFromInt(int64(metrics.WinningTrades)))
	}

	if metrics.LosingTrades > 0 {
		metrics.AvgLoss = lossPnLSum.Div(decimal.NewFromInt(int64(metrics.LosingTrades)))
	}

	// Profit factor
	if !grossLoss.IsZero() {
		metrics.ProfitFactor = grossProfit.Div(grossLoss)
	} else if grossProfit.IsPositive() {
		metrics.ProfitFactor = decimal.NewFromInt(999) // Cap at 999
	}

	// Payoff ratio (avg win / avg loss)
	if !metrics.AvgLoss.IsZero() {
		metrics.PayoffRatio = metrics.AvgWin.Div(metrics.AvgLoss.Abs())
	}

	// Expectancy
	if completedTrades > 0 {
		winProb := metrics.WinRate
		lossProb := decimal.NewFromInt(1).Sub(winProb)
		metrics.ExpectancyRatio = winProb.Mul(metrics.AvgWin).Sub(lossProb.Mul(metrics.AvgLoss.Abs()))
	}
}

// calculateReturns calculates period returns from snapshots
func (c *DefaultCalculator) calculateReturns(snapshots []*models.PortfolioSnapshot) []decimal.Decimal {
	returns := make([]decimal.Decimal, 0, len(snapshots)-1)

	for i := 1; i < len(snapshots); i++ {
		prev := snapshots[i-1].TotalValue
		curr := snapshots[i].TotalValue

		if !prev.IsZero() {
			ret := curr.Sub(prev).Div(prev)
			returns = append(returns, ret)
		}
	}

	return returns
}

// calculateRiskMetrics calculates risk-related metrics
func (c *DefaultCalculator) calculateRiskMetrics(metrics *PerformanceMetrics, returns []decimal.Decimal, snapshots []*models.PortfolioSnapshot) {
	if len(returns) == 0 {
		return
	}

	// Volatility
	metrics.VolatilityDaily = c.CalculateVolatility(returns)
	metrics.VolatilityAnnual = metrics.VolatilityDaily.Mul(decimal.NewFromFloat(math.Sqrt(c.annualizationFactor)))

	// Downside deviation
	metrics.DownsideDeviation = c.calculateDownsideDeviation(returns, decimal.Zero)

	// Max drawdown
	values := make([]decimal.Decimal, len(snapshots))
	for i, s := range snapshots {
		values[i] = s.TotalValue
	}

	maxDD, maxDDPercent := c.CalculateMaxDrawdown(values)
	metrics.MaxDrawdown = maxDD
	metrics.MaxDrawdownPercent = maxDDPercent.Mul(decimal.NewFromInt(100))

	// Ulcer Index
	metrics.UlcerIndex = c.calculateUlcerIndex(values)
}

// calculateRiskAdjustedReturns calculates risk-adjusted return metrics
func (c *DefaultCalculator) calculateRiskAdjustedReturns(metrics *PerformanceMetrics, returns []decimal.Decimal) {
	if len(returns) == 0 || metrics.VolatilityAnnual.IsZero() {
		return
	}

	// Default risk-free rate (2% annually)
	riskFreeRate := decimal.NewFromFloat(0.02 / c.annualizationFactor)

	// Sharpe Ratio
	metrics.SharpeRatio = c.CalculateSharpeRatio(returns, riskFreeRate)

	// Sortino Ratio
	metrics.SortinoRatio = c.CalculateSortinoRatio(returns, riskFreeRate, decimal.Zero)

	// Calmar Ratio
	if !metrics.MaxDrawdownPercent.IsZero() {
		annualizedReturn := meanReturn(returns).Mul(decimal.NewFromFloat(c.annualizationFactor))
		metrics.CalmarRatio = annualizedReturn.Div(metrics.MaxDrawdownPercent.Div(decimal.NewFromInt(100)))
	}
}

// calculateTimeMetrics calculates time-based metrics
func (c *DefaultCalculator) calculateTimeMetrics(metrics *PerformanceMetrics, trades []*models.Trade, startDate, endDate time.Time) {
	if len(trades) == 0 {
		return
	}

	totalDays := int(endDate.Sub(startDate).Hours() / 24)
	metrics.TotalTradingDays = totalDays

	var totalDuration time.Duration
	var maxDuration, minDuration time.Duration
	var timeBetweenSum time.Duration
	var lastExitTime time.Time
	var daysInMarket int
	var closedTradeCount int

	for i, trade := range trades {
		if trade.ExitTime == nil {
			continue
		}

		closedTradeCount++

		duration := trade.ExitTime.Sub(trade.EntryTime)
		totalDuration += duration

		if i == 0 || duration > maxDuration {
			maxDuration = duration
		}
		if i == 0 || duration < minDuration {
			minDuration = duration
		}

		// Time between trades
		if i > 0 && !lastExitTime.IsZero() {
			timeBetween := trade.EntryTime.Sub(lastExitTime)
			if timeBetween > 0 {
				timeBetweenSum += timeBetween
			}
		}
		lastExitTime = *trade.ExitTime

		// Days in market
		daysInMarket += int(duration.Hours() / 24)
	}

	if closedTradeCount > 0 {
		metrics.AvgTradeDuration = totalDuration / time.Duration(closedTradeCount)
	}
	metrics.LongestTrade = maxDuration
	metrics.ShortestTrade = minDuration

	if closedTradeCount > 1 {
		metrics.AvgTimeBetween = timeBetweenSum / time.Duration(closedTradeCount-1)
	}

	if totalDays > 0 {
		metrics.TimeInMarket = decimal.NewFromInt(int64(daysInMarket)).Div(decimal.NewFromInt(int64(totalDays))).Mul(decimal.NewFromInt(100))
	}
}

// calculateAdvancedMetrics calculates advanced performance metrics
func (c *DefaultCalculator) calculateAdvancedMetrics(metrics *PerformanceMetrics) {
	// Kelly Ratio
	if !metrics.PayoffRatio.IsZero() && !metrics.WinRate.IsZero() {
		winRate := metrics.WinRate
		lossRate := decimal.NewFromInt(1).Sub(winRate)
		numerator := winRate.Mul(metrics.PayoffRatio).Sub(lossRate)
		metrics.KellyRatio = numerator.Div(metrics.PayoffRatio)
	}

	// Recovery Factor
	if !metrics.MaxDrawdown.IsZero() {
		metrics.RecoveryFactor = metrics.NetProfit.Div(metrics.MaxDrawdown)
	}

	// Sterling Ratio (using average drawdown instead of max)
	if !metrics.MaxDrawdownPercent.IsZero() {
		annualReturn := metrics.CAGR.Div(decimal.NewFromInt(100))
		// Simplified: using max drawdown instead of average
		metrics.SterlingRatio = annualReturn.Div(metrics.MaxDrawdownPercent.Div(decimal.NewFromInt(100)))
	}
}

// CalculateSharpeRatio calculates the Sharpe ratio
func (c *DefaultCalculator) CalculateSharpeRatio(returns []decimal.Decimal, riskFreeRate decimal.Decimal) decimal.Decimal {
	if len(returns) == 0 {
		return decimal.Zero
	}

	vol := c.CalculateVolatility(returns)
	if vol.IsZero() {
		return decimal.Zero
	}

	meanRet := meanReturn(returns)
	excessReturn := meanRet.Sub(riskFreeRate)

	// Annualize
	annualExcess := excessReturn.Mul(decimal.NewFromFloat(c.annualizationFactor))
	annualVol := vol.Mul(decimal.NewFromFloat(math.Sqrt(c.annualizationFactor)))

	return annualExcess.Div(annualVol)
}

// CalculateSortinoRatio calculates the Sortino ratio
func (c *DefaultCalculator) CalculateSortinoRatio(returns []decimal.Decimal, riskFreeRate decimal.Decimal, targetReturn decimal.Decimal) decimal.Decimal {
	if len(returns) == 0 {
		return decimal.Zero
	}

	downsideDev := c.calculateDownsideDeviation(returns, targetReturn)
	if downsideDev.IsZero() {
		return decimal.Zero
	}

	meanRet := meanReturn(returns)
	excessReturn := meanRet.Sub(riskFreeRate)

	// Annualize
	annualExcess := excessReturn.Mul(decimal.NewFromFloat(c.annualizationFactor))
	annualDownside := downsideDev.Mul(decimal.NewFromFloat(math.Sqrt(c.annualizationFactor)))

	return annualExcess.Div(annualDownside)
}

// CalculateMaxDrawdown calculates maximum drawdown
func (c *DefaultCalculator) CalculateMaxDrawdown(values []decimal.Decimal) (maxDD decimal.Decimal, maxDDPercent decimal.Decimal) {
	if len(values) < 2 {
		return decimal.Zero, decimal.Zero
	}

	peak := values[0]
	maxDrawdown := decimal.Zero
	maxDrawdownPercent := decimal.Zero

	for _, value := range values[1:] {
		if value.GreaterThan(peak) {
			peak = value
		}

		drawdown := peak.Sub(value)
		if drawdown.GreaterThan(maxDrawdown) {
			maxDrawdown = drawdown
			if !peak.IsZero() {
				maxDrawdownPercent = drawdown.Div(peak)
			}
		}
	}

	return maxDrawdown, maxDrawdownPercent
}

// CalculateVolatility calculates standard deviation of returns
func (c *DefaultCalculator) CalculateVolatility(returns []decimal.Decimal) decimal.Decimal {
	if len(returns) < 2 {
		return decimal.Zero
	}

	mean := meanReturn(returns)

	// Calculate variance
	sumSquaredDiff := decimal.Zero
	for _, ret := range returns {
		diff := ret.Sub(mean)
		sumSquaredDiff = sumSquaredDiff.Add(diff.Mul(diff))
	}

	variance := sumSquaredDiff.Div(decimal.NewFromInt(int64(len(returns) - 1)))

	// Return standard deviation
	return decimalSqrt(variance)
}

// calculateDownsideDeviation calculates downside deviation
func (c *DefaultCalculator) calculateDownsideDeviation(returns []decimal.Decimal, threshold decimal.Decimal) decimal.Decimal {
	if len(returns) == 0 {
		return decimal.Zero
	}

	downsideSum := decimal.Zero
	downsideCount := 0

	for _, ret := range returns {
		if ret.LessThan(threshold) {
			diff := ret.Sub(threshold)
			downsideSum = downsideSum.Add(diff.Mul(diff))
			downsideCount++
		}
	}

	if downsideCount == 0 {
		return decimal.Zero
	}

	downsideVariance := downsideSum.Div(decimal.NewFromInt(int64(downsideCount)))
	return decimalSqrt(downsideVariance)
}

// calculateUlcerIndex calculates the Ulcer Index
func (c *DefaultCalculator) calculateUlcerIndex(values []decimal.Decimal) decimal.Decimal {
	if len(values) < 2 {
		return decimal.Zero
	}

	peak := values[0]
	sumSquaredDrawdowns := decimal.Zero

	for _, value := range values[1:] {
		if value.GreaterThan(peak) {
			peak = value
		}

		if !peak.IsZero() {
			drawdownPercent := peak.Sub(value).Div(peak)
			sumSquaredDrawdowns = sumSquaredDrawdowns.Add(drawdownPercent.Mul(drawdownPercent))
		}
	}

	avgSquaredDrawdown := sumSquaredDrawdowns.Div(decimal.NewFromInt(int64(len(values) - 1)))
	return decimalSqrt(avgSquaredDrawdown).Mul(decimal.NewFromInt(100))
}

// Helper functions

func meanReturn(returns []decimal.Decimal) decimal.Decimal {
	if len(returns) == 0 {
		return decimal.Zero
	}

	sum := decimal.Zero
	for _, ret := range returns {
		sum = sum.Add(ret)
	}

	return sum.Div(decimal.NewFromInt(int64(len(returns))))
}

// decimalSqrt calculates square root of a decimal
func decimalSqrt(d decimal.Decimal) decimal.Decimal {
	if d.IsZero() || d.IsNegative() {
		return decimal.Zero
	}

	// Convert to float64, calculate sqrt, convert back
	f, _ := d.Float64()
	return decimal.NewFromFloat(math.Sqrt(f))
}

// decimalPow calculates power of a decimal
func decimalPow(base, exponent decimal.Decimal) decimal.Decimal {
	if base.IsZero() {
		return decimal.Zero
	}

	// Convert to float64, calculate power, convert back
	b, _ := base.Float64()
	e, _ := exponent.Float64()
	return decimal.NewFromFloat(math.Pow(b, e))
}

// CalculateMonthlyReturns calculates returns grouped by month
func CalculateMonthlyReturns(snapshots []*models.PortfolioSnapshot) []MonthlyReturn {
	if len(snapshots) < 2 {
		return nil
	}

	monthlyData := make(map[string]struct {
		year       int
		month      int
		startValue decimal.Decimal
		endValue   decimal.Decimal
	})

	for i, snapshot := range snapshots {
		key := snapshot.Timestamp.Format("2006-01")
		data := monthlyData[key]

		if i == 0 || snapshot.Timestamp.Month() != snapshots[i-1].Timestamp.Month() {
			data.year = snapshot.Timestamp.Year()
			data.month = int(snapshot.Timestamp.Month())
			data.startValue = snapshot.TotalValue
		}

		data.endValue = snapshot.TotalValue
		monthlyData[key] = data
	}

	// Convert to sorted slice
	var results []MonthlyReturn
	for _, data := range monthlyData {
		if !data.startValue.IsZero() {
			ret := data.endValue.Sub(data.startValue).Div(data.startValue)
			results = append(results, MonthlyReturn{
				Year:   data.year,
				Month:  data.month,
				Return: ret.Mul(decimal.NewFromInt(100)),
			})
		}
	}

	// Sort by year and month
	sort.Slice(results, func(i, j int) bool {
		if results[i].Year != results[j].Year {
			return results[i].Year < results[j].Year
		}
		return results[i].Month < results[j].Month
	})

	return results
}

