package metrics

import (
	"testing"
	"time"

	"bigmeanie/internal/models"

	"github.com/shopspring/decimal"
	"github.com/stretchr/testify/assert"
)

func TestCalculator_Calculate_WithUnifiedTrades(t *testing.T) {
	startCapital := decimal.NewFromFloat(10000)
	endCapital := decimal.NewFromFloat(11500)
	startDate := time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC)
	endDate := time.Date(2024, 3, 1, 0, 0, 0, 0, time.UTC)

	// Create test trades from different sources
	trades := []*models.Trade{
		// Backtest trade - profit
		{
			Source:     models.TradeSourceBacktest,
			Symbol:     "BTC-USD",
			Side:       models.TradeSideBuy,
			EntryTime:  startDate.Add(24 * time.Hour),
			ExitTime:   timePtr(startDate.Add(48 * time.Hour)),
			EntryPrice: decimal.NewFromFloat(100),
			ExitPrice:  decimalPtr(decimal.NewFromFloat(110)),
			Quantity:   decimal.NewFromFloat(10),
			PnL:        decimalPtr(decimal.NewFromFloat(100)),
			Fees:       decimal.NewFromFloat(5),
			Status:     models.TradeStatusClosed,
		},
		// Paper trade - loss
		{
			Source:     models.TradeSourcePaper,
			Symbol:     "ETH-USD",
			Side:       models.TradeSideLong,
			EntryTime:  startDate.Add(72 * time.Hour),
			ExitTime:   timePtr(startDate.Add(96 * time.Hour)),
			EntryPrice: decimal.NewFromFloat(2000),
			ExitPrice:  decimalPtr(decimal.NewFromFloat(1900)),
			Quantity:   decimal.NewFromFloat(1),
			PnL:        decimalPtr(decimal.NewFromFloat(-100)),
			Fees:       decimal.NewFromFloat(10),
			Status:     models.TradeStatusClosed,
		},
		// Shadow trade - profit
		{
			Source:     models.TradeSourceShadow,
			Symbol:     "SOL-USD",
			Side:       models.TradeSideShort,
			EntryTime:  startDate.Add(120 * time.Hour),
			ExitTime:   timePtr(startDate.Add(144 * time.Hour)),
			EntryPrice: decimal.NewFromFloat(50),
			ExitPrice:  decimalPtr(decimal.NewFromFloat(45)),
			Quantity:   decimal.NewFromFloat(20),
			PnL:        decimalPtr(decimal.NewFromFloat(100)),
			Fees:       decimal.NewFromFloat(5),
			Status:     models.TradeStatusClosed,
		},
		// Live trade - still open (should be ignored in calculations)
		{
			Source:     models.TradeSourceLive,
			Symbol:     "DOGE-USD",
			Side:       models.TradeSideBuy,
			EntryTime:  startDate.Add(168 * time.Hour),
			EntryPrice: decimal.NewFromFloat(0.10),
			Quantity:   decimal.NewFromFloat(1000),
			Status:     models.TradeStatusOpen,
		},
	}

	// Create test snapshots
	snapshots := []*models.PortfolioSnapshot{
		{
			Timestamp:  startDate,
			TotalValue: startCapital,
		},
		{
			Timestamp:  startDate.Add(24 * time.Hour),
			TotalValue: decimal.NewFromFloat(10050),
		},
		{
			Timestamp:  startDate.Add(48 * time.Hour),
			TotalValue: decimal.NewFromFloat(10100),
		},
		{
			Timestamp:  endDate,
			TotalValue: endCapital,
		},
	}

	calc := NewCalculator()
	metrics := calc.Calculate(trades, snapshots, startCapital, endCapital, startDate, endDate)

	// Verify basic metrics
	assert.Equal(t, 3, metrics.TotalTrades) // Only closed trades
	assert.Equal(t, 2, metrics.WinningTrades)
	assert.Equal(t, 1, metrics.LosingTrades)

	// Win rate: 2/3 = 66.67%
	assert.True(t, metrics.WinRatePercent.GreaterThan(decimal.NewFromFloat(66)))
	assert.True(t, metrics.WinRatePercent.LessThan(decimal.NewFromFloat(67)))

	// Net profit: 100 - 100 + 100 = 100
	assert.True(t, metrics.NetProfit.Equal(decimal.NewFromFloat(100)))

	// Total return: 1500/10000 = 15%
	assert.True(t, metrics.TotalReturnPercent.Equal(decimal.NewFromFloat(15)))
}

func TestCalculator_CalculateFromBacktestTrades_BackwardCompatibility(t *testing.T) {
	startCapital := decimal.NewFromFloat(10000)
	endCapital := decimal.NewFromFloat(11000)
	startDate := time.Date(2024, 1, 1, 0, 0, 0, 0, time.UTC)
	endDate := time.Date(2024, 2, 1, 0, 0, 0, 0, time.UTC)

	// Create legacy BacktestTrade objects
	backtestTrades := []*models.BacktestTrade{
		{
			BacktestRunID: 1,
			TradeSequence: 1,
			InstrumentID:  1,
			Side:          "LONG",
			EntryTime:     startDate.Add(24 * time.Hour),
			ExitTime:      timePtr(startDate.Add(48 * time.Hour)),
			EntryPrice:    decimal.NewFromFloat(100),
			ExitPrice:     decimalPtr(decimal.NewFromFloat(110)),
			Quantity:      decimal.NewFromFloat(10),
			PnL:           decimalPtr(decimal.NewFromFloat(100)),
			Fees:          decimal.NewFromFloat(5),
			SignalType:    "RSI_OVERSOLD",
		},
	}

	snapshots := []*models.PortfolioSnapshot{
		{
			Timestamp:  startDate,
			TotalValue: startCapital,
		},
		{
			Timestamp:  endDate,
			TotalValue: endCapital,
		},
	}

	calc := NewCalculator()
	metrics := calc.CalculateFromBacktestTrades(backtestTrades, snapshots, startCapital, endCapital, startDate, endDate)

	// Verify the backward compatibility method works
	assert.Equal(t, 1, metrics.TotalTrades)
	assert.Equal(t, 1, metrics.WinningTrades)
	assert.Equal(t, 0, metrics.LosingTrades)
	assert.True(t, metrics.WinRatePercent.Equal(decimal.NewFromFloat(100)))
}

func TestCalculator_MixedTradeSources(t *testing.T) {
	// Test that the calculator correctly handles trades from multiple sources
	trades := []*models.Trade{
		createTestTrade(models.TradeSourceBacktest, 50),
		createTestTrade(models.TradeSourcePaper, -30),
		createTestTrade(models.TradeSourceShadow, 20),
		createTestTrade(models.TradeSourceLive, 40),
	}

	calc := NewCalculator()
	metrics := calc.Calculate(
		trades,
		[]*models.PortfolioSnapshot{},
		decimal.NewFromFloat(10000),
		decimal.NewFromFloat(10080),
		time.Now().Add(-30*24*time.Hour),
		time.Now(),
	)

	// Total profit: 50 - 30 + 20 + 40 = 80
	assert.True(t, metrics.NetProfit.Equal(decimal.NewFromFloat(80)))
	assert.Equal(t, 4, metrics.TotalTrades)
	assert.Equal(t, 3, metrics.WinningTrades)
	assert.Equal(t, 1, metrics.LosingTrades)
}

func TestCalculator_OnlyOpenTrades(t *testing.T) {
	// Test that open trades are excluded from calculations
	trades := []*models.Trade{
		{
			Source:     models.TradeSourceLive,
			Status:     models.TradeStatusOpen,
			EntryPrice: decimal.NewFromFloat(100),
			Quantity:   decimal.NewFromFloat(10),
		},
		{
			Source:     models.TradeSourcePaper,
			Status:     models.TradeStatusOpen,
			EntryPrice: decimal.NewFromFloat(200),
			Quantity:   decimal.NewFromFloat(5),
		},
	}

	calc := NewCalculator()
	metrics := calc.Calculate(
		trades,
		[]*models.PortfolioSnapshot{},
		decimal.NewFromFloat(10000),
		decimal.NewFromFloat(10000),
		time.Now().Add(-24*time.Hour),
		time.Now(),
	)

	// No completed trades
	assert.Equal(t, 0, metrics.TotalTrades)
	assert.Equal(t, 0, metrics.WinningTrades)
	assert.Equal(t, 0, metrics.LosingTrades)
	assert.True(t, metrics.NetProfit.IsZero())
}

// Helper functions
func createTestTrade(source string, pnl float64) *models.Trade {
	now := time.Now()
	exitTime := now.Add(-1 * time.Hour)
	pnlDecimal := decimal.NewFromFloat(pnl)

	return &models.Trade{
		Source:     source,
		Symbol:     "TEST-USD",
		Side:       models.TradeSideBuy,
		EntryTime:  now.Add(-2 * time.Hour),
		ExitTime:   &exitTime,
		EntryPrice: decimal.NewFromFloat(100),
		ExitPrice:  decimalPtr(decimal.NewFromFloat(100 + pnl/10)),
		Quantity:   decimal.NewFromFloat(10),
		PnL:        &pnlDecimal,
		Status:     models.TradeStatusClosed,
	}
}

func timePtr(t time.Time) *time.Time {
	return &t
}

func decimalPtr(d decimal.Decimal) *decimal.Decimal {
	return &d
}

