package models

import (
	"time"

	"github.com/shopspring/decimal"
)

// Trade represents a trade across all systems (backtest, paper, shadow, live)
type Trade struct {
	// Primary identification
	ID            uint            `gorm:"primaryKey" json:"id"`
	Source        string          `gorm:"size:20;not null;index" json:"source"` // "backtest", "paper", "shadow", "live"
	SourceID      *string         `gorm:"size:100;index" json:"source_id"`      // External ID (OrderID for live/paper, RunID for backtest, ProfileID for shadow)

	// Optional backtest association
	BacktestRunID *uint           `gorm:"index" json:"backtest_run_id,omitempty"`
	BacktestRun   *BacktestRun    `gorm:"foreignKey:BacktestRunID" json:"backtest_run,omitempty"`

	// Trade identification
	Symbol        string          `gorm:"size:20;not null;index" json:"symbol"`     // Direct symbol (e.g., "BTC-USD")
	InstrumentID  *uint           `gorm:"index" json:"instrument_id,omitempty"`     // Optional link to instruments table
	Instrument    *Instrument     `gorm:"foreignKey:InstrumentID" json:"instrument,omitempty"`

	// Trade details
	Side          string          `gorm:"size:10;not null" json:"side"`             // "BUY", "SELL", "LONG", "SHORT"
	EntryTime     time.Time       `gorm:"not null;index" json:"entry_time"`
	ExitTime      *time.Time      `gorm:"index" json:"exit_time"`
	EntryPrice    decimal.Decimal `gorm:"type:decimal(20,8);not null" json:"entry_price"`
	ExitPrice     *decimal.Decimal `gorm:"type:decimal(20,8)" json:"exit_price"`
	Quantity      decimal.Decimal `gorm:"type:decimal(20,8);not null" json:"quantity"`

	// Performance
	PnL           *decimal.Decimal `gorm:"type:decimal(20,8)" json:"pnl"`
	PnLPercent    *decimal.Decimal `gorm:"type:decimal(10,6)" json:"pnl_percent"`
	Fees          decimal.Decimal  `gorm:"type:decimal(20,8);default:0" json:"fees"`

	// Strategy information
	SignalType    string          `gorm:"size:50" json:"signal_type"`               // e.g., "RSI_OVERSOLD", "MEAN_REVERSION"
	EntryReason   string          `gorm:"size:200" json:"entry_reason"`
	ExitReason    string          `gorm:"size:200" json:"exit_reason"`

	// Status tracking
	Status        string          `gorm:"size:20;not null;default:'OPEN'" json:"status"` // "OPEN", "CLOSED", "CANCELLED"

	// Metadata
	ProfileID     *string         `gorm:"size:50;index" json:"profile_id,omitempty"` // For shadow trades
	AccountID     *string         `gorm:"size:50" json:"account_id,omitempty"`       // For live/paper trades

	// Timestamps
	CreatedAt     time.Time       `json:"created_at"`
	UpdatedAt     time.Time       `json:"updated_at"`
}

// TradeSource constants
const (
	TradeSourceBacktest = "backtest"
	TradeSourcePaper    = "paper"
	TradeSourceShadow   = "shadow"
	TradeSourceLive     = "live"
)

// TradeStatus constants
const (
	TradeStatusOpen      = "OPEN"
	TradeStatusClosed    = "CLOSED"
	TradeStatusCancelled = "CANCELLED"
)

// TradeSide constants
const (
	TradeSideBuy   = "BUY"
	TradeSideSell  = "SELL"
	TradeSideLong  = "LONG"
	TradeSideShort = "SHORT"
)

// TableName sets the table name for GORM
func (Trade) TableName() string {
	return "trades"
}

// IsComplete returns true if the trade has been closed
func (t *Trade) IsComplete() bool {
	return t.Status == TradeStatusClosed && t.ExitTime != nil && t.ExitPrice != nil
}

// CalculatePnL calculates the profit/loss for the trade
func (t *Trade) CalculatePnL() {
	if t.ExitPrice == nil || t.ExitPrice.IsZero() {
		return
	}

	var pnl decimal.Decimal
	if t.Side == TradeSideBuy || t.Side == TradeSideLong {
		// For buy/long: profit = (exit - entry) * quantity - fees
		pnl = t.ExitPrice.Sub(t.EntryPrice).Mul(t.Quantity).Sub(t.Fees)
	} else {
		// For sell/short: profit = (entry - exit) * quantity - fees
		pnl = t.EntryPrice.Sub(*t.ExitPrice).Mul(t.Quantity).Sub(t.Fees)
	}

	t.PnL = &pnl

	// Calculate percentage return
	if !t.EntryPrice.IsZero() {
		pnlPercent := pnl.Div(t.EntryPrice.Mul(t.Quantity)).Mul(decimal.NewFromInt(100))
		t.PnLPercent = &pnlPercent
	}
}

// GetDuration returns the duration of the trade
func (t *Trade) GetDuration() time.Duration {
	if t.ExitTime == nil {
		return time.Since(t.EntryTime)
	}
	return t.ExitTime.Sub(t.EntryTime)
}

// ConvertFromBacktestTrade converts a BacktestTrade to the Trade model
func ConvertFromBacktestTrade(bt *BacktestTrade, symbol string) *Trade {
	trade := &Trade{
		Source:        TradeSourceBacktest,
		BacktestRunID: &bt.BacktestRunID,
		Symbol:        symbol,
		InstrumentID:  &bt.InstrumentID,
		Side:          bt.Side,
		EntryTime:     bt.EntryTime,
		ExitTime:      bt.ExitTime,
		EntryPrice:    bt.EntryPrice,
		ExitPrice:     bt.ExitPrice,
		Quantity:      bt.Quantity,
		PnL:           bt.PnL,
		Fees:          bt.Fees,
		SignalType:    bt.SignalType,
		Status:        TradeStatusOpen,
		CreatedAt:     bt.CreatedAt,
		UpdatedAt:     time.Now(),
	}

	if bt.ExitTime != nil {
		trade.Status = TradeStatusClosed
	}

	return trade
}

// ToBacktestTrade converts the Trade to a BacktestTrade (for compatibility)
func (t *Trade) ToBacktestTrade(tradeSequence int) *BacktestTrade {
	if t.BacktestRunID == nil {
		return nil
	}

	return &BacktestTrade{
		BacktestRunID: *t.BacktestRunID,
		TradeSequence: tradeSequence,
		InstrumentID:  *t.InstrumentID,
		EntryTime:     t.EntryTime,
		ExitTime:      t.ExitTime,
		Side:          t.Side,
		EntryPrice:    t.EntryPrice,
		ExitPrice:     t.ExitPrice,
		Quantity:      t.Quantity,
		PnL:           t.PnL,
		SignalType:    t.SignalType,
		Fees:          t.Fees,
		CreatedAt:     t.CreatedAt,
	}
}
