    ~int | ~int8 | ~int16 | ~int32 | ~int64 |
    ~uint | ~uint8 | ~uint16 | ~uint32 | ~uint64 |
    ~float32 | ~float64
}

type Addable[T any] interface {
    Add(T) T
}

type AddableNumber[T Number] interface {
    Number
    Addable[T]
}
