   200â†’### Builder
   201â†’Builder pattern separates the construction of complex objects from their representation.
   202â†’
   203â†’```go
   204â†’// Product
   205â†’type House struct {
   206â†’    Foundation string
   207â†’    Structure  string
   208â†’    Roof       string
   209â†’    Interior   string
   210â†’    Garden     bool
   211â†’    Pool       bool
   212â†’}
   213â†’
   214â†’// Builder interface
   215â†’type HouseBuilder interface {
   216â†’    SetFoundation() HouseBuilder
   217â†’    SetStructure() HouseBuilder
   218â†’    SetRoof() HouseBuilder
   219â†’    SetInterior() HouseBuilder
   220â†’    SetGarden() HouseBuilder
   221â†’    SetPool() HouseBuilder
   222â†’    Build() *House
   223â†’}
   224â†’
   225â†’// Concrete builder
   226â†’type ModernHouseBuilder struct {
   227â†’    house *House
   228â†’}
   229â†’
   230â†’func NewModernHouseBuilder() *ModernHouseBuilder {
   231â†’    return &ModernHouseBuilder{
   232â†’        house: &House{},
   233â†’    }
   234â†’}
   235â†’
   236â†’func (b *ModernHouseBuilder) SetFoundation() HouseBuilder {
   237â†’    b.house.Foundation = "Concrete, brick, and stone"
   238â†’    return b
   239â†’}
   240â†’
   241â†’func (b *ModernHouseBuilder) SetStructure() HouseBuilder {
   242â†’    b.house.Structure = "Steel and glass"
   243â†’    return b
   244â†’}
   245â†’
   246â†’func (b *ModernHouseBuilder) SetRoof() HouseBuilder {
   247â†’    b.house.Roof = "Flat concrete"
   248â†’    return b
   249â†’}
   250â†’
   251â†’func (b *ModernHouseBuilder) SetInterior() HouseBuilder {
   252â†’    b.house.Interior = "Modern minimalist"
   253â†’    return b
   254â†’}
   255â†’
   256â†’func (b *ModernHouseBuilder) SetGarden() HouseBuilder {
   257â†’    b.house.Garden = true
   258â†’    return b
   259â†’}
   260â†’
   261â†’func (b *ModernHouseBuilder) SetPool() HouseBuilder {
   262â†’    b.house.Pool = true
   263â†’    return b
   264â†’}
   265â†’
   266â†’func (b *ModernHouseBuilder) Build() *House {
   267â†’    return b.house
   268â†’}
   269â†’
   270â†’// Director (optional)
   271â†’type ConstructionDirector struct {
   272â†’    builder HouseBuilder
   273â†’}
   274â†’
   275â†’func (d *ConstructionDirector) ConstructLuxuryHouse() *House {
   276â†’    return d.builder.
   277â†’        SetFoundation().
   278â†’        SetStructure().
   279â†’        SetRoof().
   280â†’        SetInterior().
   281â†’        SetGarden().
   282â†’        SetPool().
   283â†’        Build()
   284â†’}
   285â†’
   286â†’// Usage
   287â†’func main() {
   288â†’    builder := NewModernHouseBuilder()
   289â†’    
   290â†’    // Direct use
   291â†’    house := builder.
   292â†’        SetFoundation().
   293â†’        SetStructure().
   294â†’        SetRoof().
   295â†’        Build()
   296â†’    
   297â†’    // Using director
   298â†’    director := &ConstructionDirector{builder: builder}
   299â†’    luxuryHouse := director.ConstructLuxuryHouse()
   300â†’}
