   784â†’### Flyweight
   785â†’Flyweight minimizes memory usage by sharing efficiently large numbers of fine-grained objects.
   786â†’
   787â†’```go
   788â†’// Flyweight interface
   789â†’type Character interface {
   790â†’    Display(font string, size int, color string)
   791â†’}
   792â†’
   793â†’// Concrete flyweight
   794â†’type ConcreteCharacter struct {
   795â†’    symbol rune // Intrinsic state (shared)
   796â†’}
   797â†’
   798â†’func (c *ConcreteCharacter) Display(font string, size int, color string) {
   799â†’    // Extrinsic state (passed in)
   800â†’    fmt.Printf("Character: %c, Font: %s, Size: %d, Color: %s\n", 
   801â†’        c.symbol, font, size, color)
   802â†’}
   803â†’
   804â†’// Flyweight factory
   805â†’type CharacterFactory struct {
   806â†’    characters map[rune]*ConcreteCharacter
   807â†’}
   808â†’
   809â†’func NewCharacterFactory() *CharacterFactory {
   810â†’    return &CharacterFactory{
   811â†’        characters: make(map[rune]*ConcreteCharacter),
   812â†’    }
   813â†’}
   814â†’
   815â†’func (f *CharacterFactory) GetCharacter(symbol rune) Character {
   816â†’    if char, exists := f.characters[symbol]; exists {
   817â†’        return char
   818â†’    }
   819â†’    
   820â†’    char := &ConcreteCharacter{symbol: symbol}
   821â†’    f.characters[symbol] = char
   822â†’    return char
   823â†’}
   824â†’
   825â†’// Context that uses flyweights
   826â†’type TextDocument struct {
   827â†’    characters []Character
   828â†’    fonts      []string
   829â†’    sizes      []int
   830â†’    colors     []string
   831â†’}
   832â†’
   833â†’func (d *TextDocument) AddCharacter(char Character, font string, size int, color string) {
   834â†’    d.characters = append(d.characters, char)
   835â†’    d.fonts = append(d.fonts, font)
   836â†’    d.sizes = append(d.sizes, size)
   837â†’    d.colors = append(d.colors, color)
   838â†’}
   839â†’
   840â†’func (d *TextDocument) Display() {
   841â†’    for i, char := range d.characters {
