   864â†’### Proxy
   865â†’Proxy provides a placeholder or surrogate for another object to control access to it.
   866â†’
   867â†’```go
   868â†’// Subject interface
   869â†’type Database interface {
   870â†’    Query(sql string) ([]string, error)
   871â†’}
   872â†’
   873â†’// Real subject
   874â†’type RealDatabase struct {
   875â†’    connectionString string
   876â†’}
   877â†’
   878â†’func (d *RealDatabase) Query(sql string) ([]string, error) {
   879â†’    fmt.Printf("Executing query: %s\n", sql)
   880â†’    // Simulate database query
   881â†’    return []string{"result1", "result2"}, nil
   882â†’}
   883â†’
   884â†’// Proxy with caching
   885â†’type CachingDatabaseProxy struct {
   886â†’    database *RealDatabase
   887â†’    cache    map[string][]string
   888â†’}
   889â†’
   890â†’func NewCachingDatabaseProxy(connString string) *CachingDatabaseProxy {
   891â†’    return &CachingDatabaseProxy{
   892â†’        database: &RealDatabase{connectionString: connString},
   893â†’        cache:    make(map[string][]string),
   894â†’    }
   895â†’}
   896â†’
   897â†’func (p *CachingDatabaseProxy) Query(sql string) ([]string, error) {
   898â†’    // Check cache
   899â†’    if results, exists := p.cache[sql]; exists {
   900â†’        fmt.Printf("Returning cached results for: %s\n", sql)
   901â†’        return results, nil
   902â†’    }
   903â†’    
   904â†’    // Query real database
   905â†’    results, err := p.database.Query(sql)
   906â†’    if err != nil {
   907â†’        return nil, err
   908â†’    }
   909â†’    
   910â†’    // Cache results
   911â†’    p.cache[sql] = results
   912â†’    return results, nil
   913â†’}
   914â†’
   915â†’// Security proxy
   916â†’type SecureDatabaseProxy struct {
   917â†’    database Database
   918â†’    userRole string
   919â†’}
   920â†’
   921â†’func NewSecureDatabaseProxy(db Database, role string) *SecureDatabaseProxy {
   922â†’    return &SecureDatabaseProxy{
   923â†’        database: db,
   924â†’        userRole: role,
   925â†’    }
   926â†’}
   927â†’
   928â†’func (p *SecureDatabaseProxy) Query(sql string) ([]string, error) {
   929â†’    // Check permissions
   930â†’    if strings.Contains(strings.ToLower(sql), "delete") && p.userRole != "admin" {
   931â†’        return nil, fmt.Errorf("permission denied: only admin can delete")
   932â†’    }
   933â†’    
   934â†’    return p.database.Query(sql)
   935â†’}
   936â†’
   937â†’// Usage
   938â†’func main() {
   939â†’    // Caching proxy
   940â†’    db := NewCachingDatabaseProxy("connection-string")
   941â†’    
   942â†’    // First query hits the database
   943â†’    results, _ := db.Query("SELECT * FROM users")
   944â†’    fmt.Println("Results:", results)
   945â†’    
   946â†’    // Second identical query uses cache
   947â†’    results, _ = db.Query("SELECT * FROM users")
   948â†’    fmt.Println("Results:", results)
   949â†’    
   950â†’    // Security proxy
   951â†’    secureDB := NewSecureDatabaseProxy(db, "user")
   952â†’    _, err := secureDB.Query("DELETE FROM users")
   953â†’    if err != nil {
   954â†’        fmt.Println("Error:", err)
   955â†’    }
   956â†’}
   957â†’```
