  1347â†’### Memento
  1348â†’Memento captures and externalizes an object's internal state for later restoration.
  1349â†’
  1350â†’```go
  1351â†’// Memento
  1352â†’type EditorMemento struct {
  1353â†’    content string
  1354â†’    cursor  int
  1355â†’}
  1356â†’
  1357â†’// Originator
  1358â†’type TextEditor struct {
  1359â†’    content string
  1360â†’    cursor  int
  1361â†’}
  1362â†’
  1363â†’func (e *TextEditor) Type(text string) {
  1364â†’    e.content = e.content[:e.cursor] + text + e.content[e.cursor:]
  1365â†’    e.cursor += len(text)
  1366â†’}
  1367â†’
  1368â†’func (e *TextEditor) Delete(length int) {
  1369â†’    if e.cursor >= length {
  1370â†’        e.content = e.content[:e.cursor-length] + e.content[e.cursor:]
  1371â†’        e.cursor -= length
  1372â†’    }
  1373â†’}
  1374â†’
  1375â†’func (e *TextEditor) Save() *EditorMemento {
  1376â†’    return &EditorMemento{
  1377â†’        content: e.content,
  1378â†’        cursor:  e.cursor,
  1379â†’    }
  1380â†’}
  1381â†’
  1382â†’func (e *TextEditor) Restore(memento *EditorMemento) {
  1383â†’    e.content = memento.content
  1384â†’    e.cursor = memento.cursor
  1385â†’}
  1386â†’
  1387â†’func (e *TextEditor) String() string {
  1388â†’    return fmt.Sprintf("Content: %s (cursor at %d)", e.content, e.cursor)
  1389â†’}
  1390â†’
  1391â†’// Caretaker
  1392â†’type History struct {
  1393â†’    mementos []*EditorMemento
  1394â†’    current  int
  1395â†’}
  1396â†’
  1397â†’func (h *History) Push(memento *EditorMemento) {
  1398â†’    // Remove any states after current position
  1399â†’    h.mementos = h.mementos[:h.current+1]
  1400â†’    h.mementos = append(h.mementos, memento)
  1401â†’    h.current++
  1402â†’}
  1403â†’
  1404â†’func (h *History) Undo() *EditorMemento {
  1405â†’    if h.current > 0 {
  1406â†’        h.current--
  1407â†’        return h.mementos[h.current]
  1408â†’    }
  1409â†’    return nil
  1410â†’}
  1411â†’
  1412â†’func (h *History) Redo() *EditorMemento {
  1413â†’    if h.current < len(h.mementos)-1 {
  1414â†’        h.current++
  1415â†’        return h.mementos[h.current]
  1416â†’    }
  1417â†’    return nil
  1418â†’}
  1419â†’
  1420â†’// Usage
  1421â†’func main() {
  1422â†’    editor := &TextEditor{}
  1423â†’    history := &History{}
  1424â†’    
  1425â†’    // Initial state
  1426â†’    history.Push(editor.Save())
  1427â†’    
  1428â†’    editor.Type("Hello ")
  1429â†’    history.Push(editor.Save())
  1430â†’    
  1431â†’    editor.Type("World")
  1432â†’    history.Push(editor.Save())
  1433â†’    
  1434â†’    editor.Type("!")
  1435â†’    history.Push(editor.Save())
  1436â†’    
  1437â†’    fmt.Println(editor)
  1438â†’    
  1439â†’    // Undo
  1440â†’    if memento := history.Undo(); memento != nil {
  1441â†’        editor.Restore(memento)
  1442â†’        fmt.Println("After undo:", editor)
  1443â†’    }
  1444â†’    
  1445â†’    // Undo again
  1446â†’    if memento := history.Undo(); memento != nil {
  1447â†’        editor.Restore(memento)
  1448â†’        fmt.Println("After undo:", editor)
  1449â†’    }
  1450â†’    
  1451â†’    // Redo
  1452â†’    if memento := history.Redo(); memento != nil {
  1453â†’        editor.Restore(memento)
  1454â†’        fmt.Println("After redo:", editor)
  1455â†’    }
  1456â†’}
