  1614â†’// Concrete states
  1615â†’type IdleState struct{}
  1616â†’
  1617â†’func (s *IdleState) InsertCoin(m *VendingMachine) {
  1618â†’    fmt.Println("Coin inserted")
  1619â†’    m.coins++
  1620â†’    m.SetState(&HasCoinState{})
  1621â†’}
  1622â†’
  1623â†’func (s *IdleState) SelectProduct(m *VendingMachine) {
