  1639â†’}
  1640â†’
  1641â†’func (s *HasCoinState) SelectProduct(m *VendingMachine) {
  1642â†’    if m.products > 0 {
  1643â†’        fmt.Println("Product selected")
  1644â†’        m.SetState(&DispensingState{})
  1645â†’        m.Dispense()
