  2112â†’### Unit of Work Pattern
  2113â†’Unit of Work maintains a list of objects affected by a business transaction and coordinates writing out changes.
  2114â†’
  2115â†’```go
  2116â†’// Unit of Work interface
  2117â†’type UnitOfWork interface {
  2118â†’    UserRepository() UserRepository
  2119â†’    OrderRepository() OrderRepository
  2120â†’    Commit() error
  2121â†’    Rollback() error
  2122â†’}
  2123â†’
  2124â†’// PostgreSQL Unit of Work
  2125â†’type PostgresUnitOfWork struct {
  2126â†’    tx    *sql.Tx
  2127â†’    users *PostgresUserRepository
  2128â†’    orders *PostgresOrderRepository
  2129â†’}
  2130â†’
  2131â†’func NewPostgresUnitOfWork(db *sql.DB) (*PostgresUnitOfWork, error) {
  2132â†’    tx, err := db.Begin()
  2133â†’    if err != nil {
  2134â†’        return nil, err
  2135â†’    }
  2136â†’    
  2137â†’    return &PostgresUnitOfWork{
  2138â†’        tx:     tx,
  2139â†’        users:  &PostgresUserRepository{db: db},
  2140â†’        orders: &PostgresOrderRepository{db: db},
  2141â†’    }, nil
  2142â†’}
  2143â†’
  2144â†’func (u *PostgresUnitOfWork) UserRepository() UserRepository {
  2145â†’    return u.users
  2146â†’}
  2147â†’
  2148â†’func (u *PostgresUnitOfWork) OrderRepository() OrderRepository {
  2149â†’    return u.orders
  2150â†’}
  2151â†’
  2152â†’func (u *PostgresUnitOfWork) Commit() error {
  2153â†’    return u.tx.Commit()
  2154â†’}
  2155â†’
  2156â†’func (u *PostgresUnitOfWork) Rollback() error {
  2157â†’    return u.tx.Rollback()
  2158â†’}
  2159â†’
  2160â†’// Service using Unit of Work
  2161â†’type OrderService struct {
  2162â†’    uowFactory func() (UnitOfWork, error)
  2163â†’}
  2164â†’
  2165â†’func (s *OrderService) CreateOrder(userID string, items []OrderItem) error {
  2166â†’    uow, err := s.uowFactory()
  2167â†’    if err != nil {
  2168â†’        return err
  2169â†’    }
  2170â†’    defer uow.Rollback()
  2171â†’    
  2172â†’    // Check user exists
  2173â†’    user, err := uow.UserRepository().GetByID(context.Background(), userID)
  2174â†’    if err != nil {
  2175â†’        return err
  2176â†’    }
  2177â†’    
  2178â†’    // Create order
  2179â†’    order := &Order{
  2180â†’        UserID: user.ID,
  2181â†’        Items:  items,
  2182â†’        Total:  calculateTotal(items),
  2183â†’    }
  2184â†’    
  2185â†’    if err := uow.OrderRepository().Create(context.Background(), order); err != nil {
  2186â†’        return err
  2187â†’    }
  2188â†’    
  2189â†’    // Update user stats
  2190â†’    user.TotalOrders++
  2191â†’    if err := uow.UserRepository().Update(context.Background(), user); err != nil {
  2192â†’        return err
  2193â†’    }
  2194â†’    
  2195â†’    // Commit transaction
  2196â†’    return uow.Commit()
  2197â†’}
  2198â†’```
