### Pagination

```go
type PaginationParams struct {
    Page  int `json:"page"`
    Limit int `json:"limit"`
}

type PaginatedResponse struct {
    Data       interface{} `json:"data"`
    Pagination Pagination  `json:"pagination"`
}

type Pagination struct {
    Page       int  `json:"page"`
    Limit      int  `json:"limit"`
    Total      int  `json:"total"`
    TotalPages int  `json:"total_pages"`
    HasNext    bool `json:"has_next"`
    HasPrev    bool `json:"has_prev"`
}

func (s *Server) getPaginationParams(r *http.Request) PaginationParams {
    params := PaginationParams{
        Page:  1,
        Limit: 20,
    }

    if page := r.URL.Query().Get("page"); page != "" {
        if p, err := strconv.Atoi(page); err == nil && p > 0 {
            params.Page = p
        }
    }

    if limit := r.URL.Query().Get("limit"); limit != "" {
        if l, err := strconv.Atoi(limit); err == nil && l > 0 && l <= 100 {
            params.Limit = l
        }
    }

    return params
}

func (s *Server) listTrades(w http.ResponseWriter, r *http.Request) {
    params := s.getPaginationParams(r)

    trades, total, err := s.db.GetTrades(r.Context(), params.Page, params.Limit)
    if err != nil {
        s.respondError(w, http.StatusInternalServerError, "Failed to fetch trades")
        return
    }

    totalPages := (total + params.Limit - 1) / params.Limit

    s.respondJSON(w, http.StatusOK, PaginatedResponse{
        Data: trades,
        Pagination: Pagination{
            Page:       params.Page,
            Limit:      params.Limit,
            Total:      total,
            TotalPages: totalPages,
            HasNext:    params.Page < totalPages,
            HasPrev:    params.Page > 1,
        },
    })
}
