   679â†’    q.items = q.items[1:]
   680â†’    return item
   681â†’}
   682â†’
   683â†’// Broadcast to all waiters
   684â†’func (q *Queue) Close() {
   685â†’    q.mu.Lock()
   686â†’    defer q.mu.Unlock()
   687â†’    q.cond.Broadcast()  // Wake all waiters
   688â†’}
