   856â†’func (s *Stack) Push(v interface{}) {
   857â†’    n := &node{value: v}
   858â†’    for {
   859â†’        head := s.head.Load()
   860â†’        if head != nil {
   861â†’            n.next = head.(*node)
   862â†’        }
   863â†’        if s.head.CompareAndSwap(head, n) {
   864â†’            return
   865â†’        }
   866â†’    }
   867â†’}
   868â†’
   869â†’func (s *Stack) Pop() interface{} {
   870â†’    for {
   871â†’        head := s.head.Load()
   872â†’        if head == nil {
   873â†’            return nil
   874â†’        }
   875â†’        n := head.(*node)
   876â†’        if s.head.CompareAndSwap(head, n.next) {
   877â†’            return n.value
   878â†’        }
   879â†’    }
   880â†’}
