   971â†’// Simple rate limiter
   972â†’func rateLimiter() {
   973â†’    rate := time.Second / 10  // 10 requests per second
   974â†’    ticker := time.NewTicker(rate)
   975â†’    defer ticker.Stop()
   976â†’    
   977â†’    requests := make(chan int, 100)
   978â†’    
   979â†’    go func() {
   980â†’        for i := 0; i < 100; i++ {
   981â†’            requests <- i
   982â†’        }
   983â†’        close(requests)
   984â†’    }()
   985â†’    
   986â†’    for req := range requests {
   987â†’        <-ticker.C  // Wait for tick
   988â†’        go process(req)
