   997â†’func NewRateLimiter(rate int, burst int) *RateLimiter {
   998â†’    rl := &RateLimiter{
   999â†’        tokens: make(chan struct{}, burst),
  1000â†’    }
  1001â†’    
  1002â†’    // Fill bucket
  1003â†’    for i := 0; i < burst; i++ {
  1004â†’        rl.tokens <- struct{}{}
  1005â†’    }
  1006â†’    
  1007â†’    // Refill tokens
  1008â†’    go func() {
  1009â†’        ticker := time.NewTicker(time.Second / time.Duration(rate))
  1010â†’        defer ticker.Stop()
  1011â†’        
  1012â†’        for range ticker.C {
  1013â†’            select {
  1014â†’            case rl.tokens <- struct{}{}:
  1015â†’            default:
  1016â†’                // Bucket full
  1017â†’            }
  1018â†’        }
  1019â†’    }()
  1020â†’    
  1021â†’    return rl
