  1038â†’### Circuit Breaker
  1039â†’```go
  1040â†’type CircuitBreaker struct {
  1041â†’    mu            sync.RWMutex
  1042â†’    failures      int
  1043â†’    lastFailTime  time.Time
  1044â†’    state         string // "closed", "open", "half-open"
  1045â†’    maxFailures   int
  1046â†’    resetTimeout  time.Duration
  1047â†’}
  1048â†’
  1049â†’func NewCircuitBreaker(maxFailures int, resetTimeout time.Duration) *CircuitBreaker {
  1050â†’    return &CircuitBreaker{
  1051â†’        maxFailures:  maxFailures,
  1052â†’        resetTimeout: resetTimeout,
  1053â†’        state:        "closed",
  1054â†’    }
  1055â†’}
  1056â†’
  1057â†’func (cb *CircuitBreaker) Call(fn func() error) error {
  1058â†’    cb.mu.RLock()
  1059â†’    state := cb.state
  1060â†’    cb.mu.RUnlock()
  1061â†’    
  1062â†’    if state == "open" {
  1063â†’        cb.mu.RLock()
  1064â†’        if time.Since(cb.lastFailTime) > cb.resetTimeout {
  1065â†’            cb.mu.RUnlock()
  1066â†’            cb.mu.Lock()
  1067â†’            cb.state = "half-open"
  1068â†’            cb.failures = 0
  1069â†’            cb.mu.Unlock()
  1070â†’        } else {
  1071â†’            cb.mu.RUnlock()
  1072â†’            return errors.New("circuit breaker is open")
  1073â†’        }
  1074â†’    }
  1075â†’    
  1076â†’    err := fn()
  1077â†’    
  1078â†’    cb.mu.Lock()
  1079â†’    defer cb.mu.Unlock()
  1080â†’    
  1081â†’    if err != nil {
  1082â†’        cb.failures++
  1083â†’        cb.lastFailTime = time.Now()
  1084â†’        
  1085â†’        if cb.failures >= cb.maxFailures {
  1086â†’            cb.state = "open"
  1087â†’        }
  1088â†’        return err
  1089â†’    }
  1090â†’    
  1091â†’    if cb.state == "half-open" {
  1092â†’        cb.state = "closed"
  1093â†’    }
  1094â†’    cb.failures = 0
  1095â†’    return nil
  1096â†’}
  1097â†’```
