  1194â†’### Context Patterns
  1195â†’```go
  1196â†’// Propagating cancellation
  1197â†’func pipeline(ctx context.Context) error {
  1198â†’    ctx, cancel := context.WithCancel(ctx)
  1199â†’    defer cancel()
  1200â†’    
  1201â†’    errc := make(chan error, 3)
  1202â†’    
  1203â†’    go func() {
  1204â†’        errc <- stage1(ctx)
  1205â†’    }()
  1206â†’    
  1207â†’    go func() {
  1208â†’        errc <- stage2(ctx)
  1209â†’    }()
  1210â†’    
  1211â†’    go func() {
  1212â†’        errc <- stage3(ctx)
  1213â†’    }()
  1214â†’    
  1215â†’    // Wait for first error or all complete
  1216â†’    for i := 0; i < 3; i++ {
  1217â†’        if err := <-errc; err != nil {
  1218â†’            return err  // Cancel will stop other stages
  1219â†’        }
  1220â†’    }
  1221â†’    
  1222â†’    return nil
  1223â†’}
