  1653â†’    shard := sm.getShard(key)
  1654â†’    shard.RLock()
  1655â†’    defer shard.RUnlock()
  1656â†’    val, ok := shard.m[key]
  1657â†’    return val, ok
  1658â†’}
  1659â†’
