  1661â†’type Config struct {
  1662â†’    v atomic.Value
  1663â†’}
  1664â†’
  1665â†’func (c *Config) Load() *ConfigData {
  1666â†’    return c.v.Load().(*ConfigData)
  1667â†’}
  1668â†’
