    52â†’// BackoffStrategy defines how to calculate restart delays
    53â†’type BackoffStrategy interface {
    54â†’	NextDelay(attempt int) time.Duration
    55â†’	Reset()
    56â†’}
    57â†’
    58â†’// ExponentialBackoff implements exponential backoff with jitter
    59â†’type ExponentialBackoff struct {
    60â†’	InitialDelay time.Duration
    61â†’	MaxDelay     time.Duration
    62â†’	Multiplier   float64
    63â†’}
    64â†’
    65â†’// NextDelay returns the next delay duration
    66â†’func (e *ExponentialBackoff) NextDelay(attempt int) time.Duration {
    67â†’	if attempt <= 0 {
    68â†’		return e.InitialDelay
    69â†’	}
    70â†’
    71â†’	delay := e.InitialDelay
    72â†’	for i := 0; i < attempt; i++ {
    73â†’		delay = time.Duration(float64(delay) * e.Multiplier)
    74â†’		if delay > e.MaxDelay {
    75â†’			return e.MaxDelay
    76â†’		}
    77â†’	}
    78â†’	return delay
    79â†’}
    80â†’
    81â†’// Reset resets the backoff state (no-op for exponential)
    82â†’func (e *ExponentialBackoff) Reset() {}
    83â†’
    84â†’// LinearBackoff implements linear backoff
    85â†’type LinearBackoff struct {
    86â†’	InitialDelay time.Duration
    87â†’	Increment    time.Duration
    88â†’	MaxDelay     time.Duration
    89â†’}
    90â†’
    91â†’// NextDelay returns the next delay duration
    92â†’func (l *LinearBackoff) NextDelay(attempt int) time.Duration {
    93â†’	delay := l.InitialDelay + time.Duration(attempt)*l.Increment
    94â†’	if delay > l.MaxDelay {
    95â†’		return l.MaxDelay
    96â†’	}
    97â†’	return delay
    98â†’}
    99â†’
   100â†’// Reset resets the backoff state (no-op for linear)
   101â†’func (l *LinearBackoff) Reset() {}
