   103â†’// DefaultBackoff returns a sensible default backoff strategy
   104â†’func DefaultBackoff() BackoffStrategy {
   105â†’	return &ExponentialBackoff{
   106â†’		InitialDelay: 100 * time.Millisecond,
   107â†’		MaxDelay:     30 * time.Second,
   108â†’		Multiplier:   2.0,
   109â†’	}
