   112â†’// SafeGo runs a function in a goroutine with panic recovery
   113â†’func SafeGo(name string, fn func(), handlers ...RecoveryHandler) {
   114â†’	handler := defaultRecoveryHandler
   115â†’	if len(handlers) > 0 {
   116â†’		handler = handlers[0]
   117â†’	}
   118â†’
   119â†’	totalGoroutines.Add(1)
   120â†’	activeGoroutines.Add(1)
   121â†’
   122â†’	go func() {
   123â†’		defer activeGoroutines.Add(-1)
   124â†’		defer func() {
   125â†’			if r := recover(); r != nil {
   126â†’				stack := debug.Stack()
   127â†’				handler(r, stack)
   128â†’				log.Errorf("Goroutine '%s' panicked and was recovered", name)
   129â†’			}
   130â†’		}()
   131â†’
   132â†’		log.Debugf("Starting goroutine: %s", name)
   133â†’		fn()
   134â†’		log.Debugf("Goroutine completed: %s", name)
   135â†’	}()
   136â†’}
