   138â†’// SafeGoContext runs a function with context support and panic recovery
   139â†’func SafeGoContext(ctx context.Context, name string, fn func(context.Context), handlers ...RecoveryHandler) {
   140â†’	handler := defaultRecoveryHandler
   141â†’	if len(handlers) > 0 {
   142â†’		handler = handlers[0]
   143â†’	}
   144â†’
   145â†’	totalGoroutines.Add(1)
   146â†’	activeGoroutines.Add(1)
   147â†’
   148â†’	go func() {
   149â†’		defer activeGoroutines.Add(-1)
   150â†’		defer func() {
   151â†’			if r := recover(); r != nil {
   152â†’				stack := debug.Stack()
   153â†’				handler(r, stack)
   154â†’				log.Errorf("Goroutine '%s' panicked and was recovered", name)
   155â†’			}
   156â†’		}()
   157â†’
   158â†’		log.Debugf("Starting goroutine: %s", name)
