   164â†’// SafeGoWithRestart runs a function with automatic restart on panic
   165â†’// Uses iterative approach to prevent goroutine accumulation
   166â†’func SafeGoWithRestart(ctx context.Context, name string, fn func(context.Context) error, maxRestarts int, backoff BackoffStrategy, handlers ...RecoveryHandler) {
   167â†’	handler := defaultRecoveryHandler
   168â†’	if len(handlers) > 0 {
   169â†’		handler = handlers[0]
   170â†’	}
   171â†’
   172â†’	if backoff == nil {
   173â†’		backoff = DefaultBackoff()
   174â†’	}
   175â†’
   176â†’	totalGoroutines.Add(1)
   177â†’	activeGoroutines.Add(1)
   178â†’
   179â†’	go func() {
   180â†’		defer activeGoroutines.Add(-1)
   181â†’		restartCount := 0
   182â†’
   183â†’		for {
   184â†’			// Check if context is cancelled
   185â†’			select {
   186â†’			case <-ctx.Done():
   187â†’				log.Infof("Goroutine '%s' stopped due to context cancellation", name)
   188â†’				return
   189â†’			default:
   190â†’			}
   191â†’
   192â†’			// Run the function with panic recovery
   193â†’			panicked := false
   194â†’			func() {
   195â†’				defer func() {
   196â†’					if r := recover(); r != nil {
   197â†’						panicked = true
   198â†’						stack := debug.Stack()
   199â†’						handler(r, stack)
   200â†’						log.Errorf("Goroutine '%s' panicked (restart %d/%d)", name, restartCount, maxRestarts)
   201â†’					}
   202â†’				}()
   203â†’
   204â†’				if restartCount == 0 {
   205â†’					log.Debugf("Starting goroutine: %s", name)
   206â†’				} else {
   207â†’					log.Debugf("Restarting goroutine: %s (attempt %d/%d)", name, restartCount, maxRestarts)
   208â†’				}
   209â†’
   210â†’				if err := fn(ctx); err != nil {
   211â†’					log.Errorf("Goroutine '%s' returned error: %v", name, err)
   212â†’				}
   213â†’			}()
   214â†’
   215â†’			// If function completed without panic, we're done
   216â†’			if !panicked {
   217â†’				log.Debugf("Goroutine completed: %s", name)
   218â†’				return
   219â†’			}
   220â†’
   221â†’			// Check if we should restart
   222â†’			if restartCount >= maxRestarts {
   223â†’				log.Errorf("Goroutine '%s' exceeded max restarts (%d), stopping", name, maxRestarts)
   224â†’				return
   225â†’			}
   226â†’
   227â†’			// Calculate backoff delay
   228â†’			delay := backoff.NextDelay(restartCount)
   229â†’			restartCount++
   230â†’			totalRestarts.Add(1)
   231â†’
   232â†’			log.Warnf("Restarting goroutine '%s' after %v delay (attempt %d/%d)", name, delay, restartCount, maxRestarts)
   233â†’
   234â†’			// Wait with context cancellation support
   235â†’			select {
   236â†’			case <-ctx.Done():
   237â†’				log.Infof("Goroutine '%s' stopped during restart delay due to context cancellation", name)
   238â†’				return
   239â†’			case <-time.After(delay):
   240â†’				// Continue to restart
   241â†’			}
   242â†’		}
   243â†’	}()
   244â†’}
