   246â†’// Group manages a group of goroutines with panic recovery and context support
   247â†’type Group struct {
   248â†’	ctx     context.Context
   249â†’	cancel  context.CancelFunc
   250â†’	wg      sync.WaitGroup
   251â†’	name    string
