   257â†’// NewGroup creates a new goroutine group with context
   258â†’func NewGroup(ctx context.Context, name string, handlers ...RecoveryHandler) *Group {
   259â†’	handler := defaultRecoveryHandler
   260â†’	if len(handlers) > 0 {
   261â†’		handler = handlers[0]
   262â†’	}
   263â†’
   264â†’	groupCtx, cancel := context.WithCancel(ctx)
   265â†’
   266â†’	return &Group{
