   275â†’// Go runs a function in the group with panic recovery
   276â†’func (g *Group) Go(taskName string, fn func(context.Context)) {
   277â†’	g.wg.Add(1)
   278â†’
   279â†’	g.mu.Lock()
   280â†’	g.tasks[taskName] = true
   281â†’	g.mu.Unlock()
   282â†’
   283â†’	totalGoroutines.Add(1)
   284â†’	activeGoroutines.Add(1)
   285â†’
   286â†’	go func() {
   287â†’		defer g.wg.Done()
   288â†’		defer activeGoroutines.Add(-1)
   289â†’		defer func() {
   290â†’			g.mu.Lock()
   291â†’			delete(g.tasks, taskName)
   292â†’			g.mu.Unlock()
   293â†’		}()
   294â†’		defer func() {
   295â†’			if r := recover(); r != nil {
   296â†’				stack := debug.Stack()
   297â†’				g.handler(r, stack)
   298â†’				log.Errorf("Task '%s' in group '%s' panicked and was recovered", taskName, g.name)
   299â†’			}
   300â†’		}()
   301â†’
   302â†’		log.Debugf("Starting task '%s' in group '%s'", taskName, g.name)
   303â†’		fn(g.ctx)
   304â†’		log.Debugf("Task '%s' in group '%s' completed", taskName, g.name)
   305â†’	}()
   306â†’}
   307â†’
   308â†’// Wait waits for all goroutines in the group to complete
   309â†’func (g *Group) Wait() {
   310â†’	g.wg.Wait()
   311â†’}
   312â†’
   313â†’// Stop cancels the group's context and waits for all tasks to complete
   314â†’func (g *Group) Stop() {
   315â†’	g.cancel()
   316â†’	g.wg.Wait()
   317â†’}
