   384â†’// SetBackoff sets a custom backoff strategy for error recovery
   385â†’func (m *Monitor) SetBackoff(backoff BackoffStrategy) *Monitor {
   386â†’	m.backoff = backoff
   387â†’	return m
   388â†’}
   389â†’
   390â†’// Start starts the monitor
   391â†’func (m *Monitor) Start() error {
   392â†’	if !m.running.CompareAndSwap(false, true) {
   393â†’		return fmt.Errorf("monitor '%s' is already running", m.name)
   394â†’	}
   395â†’
   396â†’	totalGoroutines.Add(1)
   397â†’	activeGoroutines.Add(1)
   398â†’
   399â†’	go func() {
   400â†’		defer close(m.done)
   401â†’		defer m.running.Store(false)
   402â†’		defer activeGoroutines.Add(-1)
   403â†’		defer func() {
   404â†’			if r := recover(); r != nil {
   405â†’				stack := debug.Stack()
   406â†’				m.handler(r, stack)
   407â†’				log.Errorf("Monitor '%s' panicked and stopped", m.name)
   408â†’			}
   409â†’		}()
   410â†’
   411â†’		log.Infof("Starting monitor: %s", m.name)
   412â†’
   413â†’		ticker := time.NewTicker(m.interval)
   414â†’		defer ticker.Stop()
   415â†’
   416â†’		consecutiveErrors := 0
   417â†’
   418â†’		for {
   419â†’			select {
   420â†’			case <-m.ctx.Done():
   421â†’				log.Infof("Stopping monitor '%s': context cancelled", m.name)
   422â†’				return
   423â†’			case <-ticker.C:
   424â†’				// Run the monitored function with panic recovery
   425â†’				err := func() (err error) {
   426â†’					defer func() {
   427â†’						if r := recover(); r != nil {
   428â†’							stack := debug.Stack()
   429â†’							m.handler(r, stack)
   430â†’							err = fmt.Errorf("panic: %v", r)
   431â†’						}
   432â†’					}()
   433â†’					return m.fn(m.ctx)
   434â†’				}()
   435â†’
   436â†’				if err != nil {
   437â†’					consecutiveErrors++
   438â†’					log.Errorf("Monitor '%s' error (consecutive: %d): %v", m.name, consecutiveErrors, err)
   439â†’					if m.errHandler != nil {
   440â†’						m.errHandler(err)
   441â†’					}
   442â†’
   443â†’					// Apply backoff for consecutive errors
   444â†’					if consecutiveErrors > 1 {
   445â†’						backoffDelay := m.backoff.NextDelay(consecutiveErrors - 1)
   446â†’						log.Warnf("Monitor '%s' backing off for %v due to consecutive errors", m.name, backoffDelay)
   447â†’
   448â†’						select {
   449â†’						case <-m.ctx.Done():
   450â†’							return
   451â†’						case <-time.After(backoffDelay):
   452â†’							// Continue after backoff
   453â†’						}
   454â†’					}
   455â†’				} else {
   456â†’					// Reset on success
   457â†’					if consecutiveErrors > 0 {
   458â†’						log.Infof("Monitor '%s' recovered after %d consecutive errors", m.name, consecutiveErrors)
   459â†’						consecutiveErrors = 0
   460â†’						m.backoff.Reset()
   461â†’					}
   462â†’				}
   463â†’			}
   464â†’		}
   465â†’	}()
   466â†’
   467â†’	return nil
   468â†’}
   469â†’
   470â†’// Stop stops the monitor and waits for it to complete
   471â†’func (m *Monitor) Stop() {
   472â†’	if m.running.Load() {
   473â†’		log.Infof("Stopping monitor: %s", m.name)
   474â†’		m.cancel()
   475â†’		<-m.done
   476â†’	}
   477â†’}
   478â†’
   479â†’// IsRunning returns whether the monitor is currently running
   480â†’func (m *Monitor) IsRunning() bool {
   481â†’	return m.running.Load()
   482â†’}
