   516â†’// RegisteredRoutine represents a tracked goroutine
   517â†’type RegisteredRoutine struct {
   518â†’	Name      string
   519â†’	StartTime time.Time
   520â†’	Context   context.Context
   521â†’	Cancel    context.CancelFunc
   522â†’}
