   524â†’// globalRegistry is the singleton registry instance
   525â†’var globalRegistry = &Registry{
   526â†’	routines: make(map[string]*RegisteredRoutine),
   527â†’}
   528â†’
   529â†’// Register adds a goroutine to the registry
   530â†’func Register(name string, ctx context.Context, cancel context.CancelFunc) {
   531â†’	globalRegistry.mu.Lock()
   532â†’	defer globalRegistry.mu.Unlock()
   533â†’
   534â†’	globalRegistry.routines[name] = &RegisteredRoutine{
   535â†’		Name:      name,
   536â†’		StartTime: time.Now(),
