   550â†’// StopAll cancels all registered goroutines
   551â†’func StopAll() {
   552â†’	globalRegistry.mu.RLock()
   553â†’	defer globalRegistry.mu.RUnlock()
   554â†’
   555â†’	for name, routine := range globalRegistry.routines {
   556â†’		log.Infof("Cancelling registered goroutine: %s", name)
   557â†’		routine.Cancel()
   558â†’	}
   559â†’}
