   573â†’// TrackedGo runs a goroutine with registration and automatic cleanup
   574â†’func TrackedGo(ctx context.Context, name string, fn func(context.Context), handlers ...RecoveryHandler) {
   575â†’	routineCtx, cancel := context.WithCancel(ctx)
   576â†’	Register(name, routineCtx, cancel)
   577â†’
   578â†’	handler := defaultRecoveryHandler
